/**
 *
 * Navigation.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website Navigation Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../constants/Theme";
import { Root } from "../../constants/Root";

// Begin Styles
//////////////////////////////////////////////////////////////////////

let navFontSize = "60px";

export const NavigationStyle = styled.nav`
  color: ${Root.Color.Mbm};
  mix-blend-mode: exclusion;

  a {
    text-transform: uppercase;
    color: ${Root.Color.Mbm};
  }

  .top {
    display: flex;
    justify-content: space-between;

    .left,
    .right,
    .center {
      height: calc(${navFontSize} + 10px);
      top: 0;
      position: fixed;
    }

    .left {
      display: flex;
      flex-direction: column;
      left: 0;
      padding-left: 20px;
      text-transform: uppercase;
      justify-content: center;

      span {
        &:first-child {
          font-weight: bold;
        }
        &:last-child {
          margin-top: 5px;
          font-size: 11px;
        }
      }
    }

    .right {
      display: flex;
      flex-direction: column;
      left: 0;
      padding-left: ${Root.Grid.Gutter.Left};
      text-transform: uppercase;
      justify-content: center;

      span {
        &:first-child {
          font-weight: bold;
        }
        &:last-child {
          font-size: 11px;
        }
      }
    }

    .center {
      display: flex;
      width: 100%;
      justify-content: center;
      align-items: center;
      font-size: ${navFontSize};
      text-transform: uppercase;
      font-weight: bold;
      left: 50%;
      transform: translateX(-50%);
    }
  }

  .sub {
    position: fixed;
    top: calc(${navFontSize} + 30px);
    left: 0;
    right: 0;

    .wrapper {
      max-width: ${Theme.Base.Grid.SiteWidth};
      width: 100%;
      margin: 0 auto;
      padding-left: ${Root.Grid.Gutter.Left};
      padding-right: ${Root.Grid.Gutter.Right};
      display: flex;
      justify-content: space-between;
    }
  }

  .left-side,
  .right-side {
    position: fixed;
    top: 0;
    bottom: 0;
    width: ${navFontSize};
    display: flex;
    align-items: center;
    font-size: ${navFontSize};

    a {
      transform-origin: center center;
      line-height: ${navFontSize};
    }
  }

  .left-side {
    left: 0;

    a {
      transform: rotate(-90deg) translateY(-50%);
    }
  }

  .right-side {
    right: 0;
    a {
      transform: rotate(90deg) translateY(170%);
    }
  }
`;
