/**
 *
 * Navigation.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website Navigation Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import React, { Component } from "react";
import { createGlobalStyle } from "styled-components";
import dynamic from "next/dynamic";

// Components

import { FrameEight } from "../../../components/25/8";

// Begin Component
//////////////////////////////////////////////////////////////////////

const currentSketch = 8;
const theme = {
  textColor: "#0d132a",
  bgColor: "#d5fdd2"
};

const HideNav = createGlobalStyle`
  .nav {
    display: none;
  }
`;

// @ts-ignore
const P5Wrapper = dynamic(import("react-p5-wrapper"), {
  loading: () => <p>Loading...</p>,
  ssr: false
});

class s8 extends Component<{}, any> {
  constructor(props: any) {
    super(props);

    this.state = {
      domLoaded: false,
      windowWidth: 0,
      windowHeight: 0
    };
  }

  static async getInitialProps(ctx: any) {
    const sketchId = ctx.query.sketchId;
    return {
      sketchId
    };
  }

  componentDidMount() {
    // @ts-ignore
    this.setState({
      windowWidth: window.innerWidth,
      windowHeight: window.innerHeight
    });
  }

  renderP5 = () => {
    // @ts-ignore
    const sketch = require("../../../sketches/p5/d8").default(
      this.state.windowWidth,
      this.state.windowHeight
    );
    // @ts-ignore
    return <P5Wrapper sketch={sketch} />;
  };

  render() {
    return (
      <>
        <HideNav />
        <FrameEight theme={theme} currentSketch={currentSketch} />
        <div className="container">{this.renderP5()}</div>
      </>
    );
  }
}

export default s8;
