// Core
import Link from "next/link";
import { NextRouter } from "next/router";
import React, { Component } from "react";
import { __DEBUG__ } from "../../../constants/site/Settings";
import { ThemePicker } from "../../ThemePicker";

// Styles
import {
  InteractiveFrameHeaderClassName,
  InteractiveFrameHeaderStyle,
  LayeredSidebarClassName,
  LayeredSidebarStyle,
} from "./styles.scss";

// Begin Types
// __________________________________________________________________________________________

type LXLT_InteractiveFrameHeaderDisplay = {
  isInactive?: boolean;
  isExpanded?: boolean;
  isOverlayVisible?: boolean;
  isSidebarVisible?: boolean;

  router?: NextRouter;

  eyeball?: {
    translateX: number;
    translateY: number;
  };
};

type LXLT_InteractiveFrameHeader = {
  router: NextRouter;
};

type LXLT_InteractiveFrameHeader_State = LXLT_InteractiveFrameHeaderDisplay & {};

type LXLT_InteractiveFrameItem = {
  label: string;
  href?: string;
};

type LXLT_SidebarPaneItem = {
  label: string;
  href: string;
  isMajor: boolean;
};

type LXLT_SidebarPane = {
  header?: string;
  items?: LXLT_SidebarPaneItem[];
};

// Begin Data
// __________________________________________________________________________________________

const FrameItems = () => {
  let items: LXLT_InteractiveFrameItem[] = [
    {
      label: "Studio",
      href: "/sample",
    },
    {
      label: "Art",
      href: "/sample",
    },
    {
      label: "Design",
      href: "/sample",
    },
    {
      label: "Strategy",
      href: "/sample",
    },
    {
      label: "Interactive",
      href: "/sample",
    },
    {
      label: "Murals",
      href: "/sample",
    },
    {
      label: "Code",
      href: "/sample",
    },
    {
      label: "Lab",
      href: "/sample",
    },
    {
      label: "Products",
      href: "/sample",
    },
    {
      label: "Music",
      href: "/sample",
    },
    {
      label: "Tattoo",
      href: "/sample",
    },
    {
      label: "Shop",
      href: "/sample",
    },
    {
      label: "Resources",
      href: "/sample",
    },
    {
      label: "Advice",
      href: "/sample",
    },
    {
      label: "Fonts",
      href: "/sample",
    },
    {
      label: "Contact",
      href: "/sample",
    },
  ];

  return (
    <div className={`${InteractiveFrameHeaderClassName}__marquee-container`}>
      <ul className={`${InteractiveFrameHeaderClassName}__frame-items`}>
        {items.map((item: LXLT_InteractiveFrameItem, idx: number) => {
          return (
            <li key={idx}>
              <Link href={item.href}>
                <a>{item.label}</a>
              </Link>
            </li>
          );
        })}
      </ul>

      <ul className={`${InteractiveFrameHeaderClassName}__frame-items`}>
        {items.map((item: LXLT_InteractiveFrameItem, idx: number) => {
          return (
            <li key={idx}>
              <Link href={item.href}>
                <a>{item.label}</a>
              </Link>
            </li>
          );
        })}
      </ul>
    </div>
  );
};

const LayeredSideBar = ({ addClass }) => {
  let panes: LXLT_SidebarPane[] = [
    {
      header: "Design",
      items: [
        {
          label: "Strategy",
          href: "/",
          isMajor: true,
        },
        {
          label: "Branding",
          href: "/",
          isMajor: true,
        },
        {
          label: "Illustration",
          href: "/",
          isMajor: true,
        },
        {
          label: "Packaging",
          href: "/",
          isMajor: true,
        },

        {
          label: "Creative Direction",
          href: "/",
          isMajor: false,
        },
        {
          label: "Linework",
          href: "/",
          isMajor: false,
        },
        {
          label: "Hospitality",
          href: "/",
          isMajor: false,
        },
      ],
    },

    {
      // header: "Pane 02",
      items: [
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
      ],
    },

    {
      // header: "Pane 02",
      items: [
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
      ],
    },
  ];

  if (panes) {
    return (
      <LayeredSidebarStyle
        className={`${LayeredSidebarClassName} ${addClass && addClass}`}
      >
        <div className={`${LayeredSidebarClassName}__inner`}>
          {panes.map((pane: LXLT_SidebarPane, idx: number) => {
            let paneItemsMajor: LXLT_SidebarPaneItem[] = pane.items
              ? pane.items.filter((item: LXLT_SidebarPaneItem) => item.isMajor)
              : [];

            let paneItemsMinor: LXLT_SidebarPaneItem[] = pane.items
              ? pane.items.filter((item: LXLT_SidebarPaneItem) => !item.isMajor)
              : [];

            return (
              <>
                {/* ____________________________________________________________ */}
                {/* Navigaton Pane */}
                <div className={`${LayeredSidebarClassName}__pane`} key={idx}>
                  {/* ____________________________________________________________ */}
                  {/* List header */}
                  {pane.header && (
                    <div className={`${LayeredSidebarClassName}__pane__header`}>
                      {pane.header}
                    </div>
                  )}

                  {/* ____________________________________________________________ */}
                  {/* List items */}
                  {pane.items && (
                    <>
                      {paneItemsMajor.length > 0 && (
                        <ul
                          className={`${LayeredSidebarClassName}__pane__list ${LayeredSidebarClassName}__pane__list--major`}
                        >
                          {paneItemsMajor.map(
                            (paneItem: LXLT_SidebarPaneItem, idxx: number) => {
                              return (
                                <li
                                  className={`${LayeredSidebarClassName}__pane__list__item ${LayeredSidebarClassName}__pane__list__item--${
                                    paneItem.isMajor ? "major" : "minor"
                                  }`}
                                  key={idxx}
                                >
                                  <Link href={paneItem.href}>
                                    <a>{paneItem.label}</a>
                                  </Link>
                                </li>
                              );
                            }
                          )}
                        </ul>
                      )}

                      {paneItemsMinor.length > 0 && (
                        <ul
                          className={`${LayeredSidebarClassName}__pane__list ${LayeredSidebarClassName}__pane__list--minor`}
                        >
                          {paneItemsMinor.map(
                            (paneItem: LXLT_SidebarPaneItem, idxx: number) => {
                              return (
                                <li
                                  className={`${LayeredSidebarClassName}__pane__list__item ${LayeredSidebarClassName}__pane__list__item--${
                                    paneItem.isMajor ? "major" : "minor"
                                  }`}
                                  key={idxx}
                                >
                                  <Link href={paneItem.href}>
                                    <a>{paneItem.label}</a>
                                  </Link>
                                </li>
                              );
                            }
                          )}
                        </ul>
                      )}
                    </>
                  )}
                </div>
              </>
            );
          })}
        </div>
      </LayeredSidebarStyle>
    );
  } else {
    return null;
  }
};

