/**
 *
 * Listings.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website Listings Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled from "styled-components";

// Constants
// import { Theme } from "../../constants/Theme";
// import { Root } from "../../constants/Root";

// Begin Interface
//////////////////////////////////////////////////////////////////////

interface Listing {
  width: string;
  theme: string;
}

// Begin Styles
//////////////////////////////////////////////////////////////////////

export const ListingsStyle = styled.div`
  .inner {
    width: 100%;
    padding-top: 150px;
    display: flex;
    flex-wrap: wrap;
  }
`;

export const ListingStyle = styled.a`
  display: flex;
  flex: ${(props: Listing) => props.width};
  background: ${(props: Listing) => props.theme};
  height: auto;

  .listing-inner {
    img {
      max-width: 100%;
    }
  }
`;
