  scrollX: number;
  scrollY: number;

  pupilWidth: number;
  pupilHeight: number;

  pupilOffsetX: number;
  pupilOffsetY: number;

  rotation: number;
};

type LXLT_InteractiveEyeball = {
  addClass?: string;
};

// Begin Data
// __________________________________________________________________________________________

