// Core
import React from "react";
import {
  DharmaTypeClassName,
  DharmaTypeStyle,
} from "../../_svg/DharmaType/styles.scss";

// Begin Types
// __________________________________________________________________________________________

type LXLT_DharmaCounter = {
  text: string;
};

type LXLT_DharmaCounterState = {};

// Begin Component
// __________________________________________________________________________________________

/**
 * @name DharmaCounter
 * @author Peter Laxalt
 *
 */
export class DharmaCounter extends React.PureComponent<
  LXLT_DharmaCounter,
  LXLT_DharmaCounterState
> {
  render() {
    let { text } = this.props;

    let viewBoxHeight = 173.21;
    let viewBoxWidth = 215.21;

    let characterArray = text.split("");
    let characterCount = characterArray.length;
    let characterWidth = viewBoxWidth / characterCount;

    console.log("characterArray / ", characterArray);
    console.log("characterCount / ", characterCount);

    return (
      <DharmaTypeStyle
        xmlns="http://www.w3.org/2000/svg"
        viewBox={`0 0 ${viewBoxWidth} ${viewBoxHeight}`}
        preserveAspectRatio="none"
        className={`${DharmaTypeClassName} ${DharmaTypeClassName}--couter`}
        style={{
          [`--${DharmaTypeClassName}-font-size` as any]: `${viewBoxHeight}px`,
        }}
      >
        <g>
          {characterArray.map((char: string, idx: number) => {
            return (
              <g className={`${DharmaTypeClassName}__character-wrapper`}>
                <text
                  className={`${DharmaTypeClassName}__character`}
                  transform={`translate(${
                    characterWidth * idx
                  } 170.53) scale(1.13, 1.38)`}
                  style={{
                    [`--${DharmaTypeClassName}-key` as any]: idx,
                  }}
                  key={idx}
                >
                  <tspan className={`${DharmaTypeClassName}__letter`}>
                    {char}
                  </tspan>
                </text>
              </g>
            );
          })}
        </g>
      </DharmaTypeStyle>
    );
  }
}
