/**
 *
 * Shader fun
 * @author Peter Laxalt
 *
 */

import p5 from "p5";

// Begin Component
// __________________________________________________________________________

export type LXLT_Dharma_Canvas = (
  W: number,
  H: number,
  ID: number,
  BG: string
) => (p: p5) => void;

const Dharma_Canvas: LXLT_Dharma_Canvas = (W, H, ID, BG) => (p: p5) => {
  // _________________________________________________
  // Config
  // let color = {
  //   primary: "black",
  //   primaryAlpha: "black",
  //   secondary: "#000000",
  //   bg: "cyan",
  //   white: "#ffffff",
  // };

  // _________________________________________________
  // Preload

  // _________________________________________________
  // Setup
  p.setup = () => {
    // Our Canvas
    p.createCanvas(W, H, p.WEBGL);
    p.noStroke();
  };

  // _________________________________________________
  // Draw
  p.draw = () => {
    // rect gives us some geometry on the screen

    p.background(BG);
    p.textSize(32);
    p.text("word", 10, 30);
    p.fill(0, 102, 153);

    p.fill(255, 255, 255);
    p.rect(W / -2, H / -2, W, H);
  };

  p.windowResized = () => {
    p.resizeCanvas(W, H);
  };
};

export default Dharma_Canvas;
