/**
 *
 * Shader fun
 * @author Peter Laxalt
 *
 */

import p5, { Font } from "p5";

// Begin Component
// __________________________________________________________________________

export type LXLT_DharmaCanvas = {
  W: number;
  H: number;
  ID: number;
  BG: string;
};

export type LXLT_DharmaCanvasDisplay = (
  W: number,
  H: number,
  ID: number,
  BG: string
) => (p: p5) => void;

const DharmaCanvasDisplay: LXLT_DharmaCanvasDisplay = (W, H, ID, BG) => (
  p: p5
) => {
  // _________________________________________________
  // Config
  let dharmaFont: Font;
  let text = "LAXALT";

  // _________________________________________________
  // Preload
  p.preload = () => {
    dharmaFont = p.loadFont(`/fonts/dharma/dharma_regular.ttf`);
  };

  // _________________________________________________
  // Setup
  p.setup = () => {
    // Our Canvas
    p.createCanvas(W, H, p.WEBGL);
    p.noStroke();
  };

  // _________________________________________________
  // Draw
  p.draw = () => {
    // rect gives us some geometry on the screen

    p.background(BG);
    p.textFont(dharmaFont);
    p.textLeading(0);
    p.textSize(H * 1.25);
    p.text(text, 0, -40);
    p.fill("black");
    p.textAlign(p.CENTER, p.CENTER);
  };

  p.windowResized = () => {
    p.resizeCanvas(W, H);
  };
};

export default DharmaCanvasDisplay;
