/**
 *
 * Footer.js
 * @author Peter Laxalt
 * @description The website footer.
 *
 */

// Core
import React from "react";
import { OvalButton } from "../OvalButton";

// Styles
import { FooterClassName, FooterStyle } from "./styles.scss";

// Begin Component
//////////////////////////////////////////////////////////////////////

export const Footer = () => {
  return (
    <FooterStyle className={`${FooterClassName}`}>
      <div className={`${FooterClassName}__inner`}>

        {/* ____________________________________________________________ */}
        {/* Footer Bottom */}
        <div className={`${FooterClassName}__bottom`}>
          {/* ____________________________________________________________ */}
          {/* Footer Column */}
          <div
            className={`${FooterClassName}__bottom__col ${FooterClassName}__bottom__col--one`}
          >
            <span className={`${FooterClassName}__bottom__col__label`}>Currently / Brooklyn, NYC</span>
            <span className={`${FooterClassName}__bottom__col__label`}>Originally / Reno, Nevada</span>
          </div>

          {/* ____________________________________________________________ */}
          {/* Footer Column */}
          <div
            className={`${FooterClassName}__bottom__col ${FooterClassName}__bottom__col--two`}
          >
            <strong>STUDIO LAXALT</strong>
            <span className={`${FooterClassName}__bottom__col__label`}>Â©2020 ALL RIGHTS RESERVED</span>
          </div>

          {/* ____________________________________________________________ */}
          {/* Footer Column */}
          <div
            className={`${FooterClassName}__bottom__col ${FooterClassName}__bottom__col--three`}
          >
            <OvalButton
              label={`Back to top`}
              addClass={`${FooterClassName}__oval-btn`}
              invert={false}
            />
          </div>
        </div>
      </div>
    </FooterStyle>
  );
};

// End Component
//////////////////////////////////////////////////////////////////////
