// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../constants/Theme";
import { Root } from "../../constants/Root";
import {} from "../../constants/styles/CssUtils";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name MellowFrameHeaderClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const MellowFrameHeaderClassName = "mellow-frame-header";
export const CssFrameBorderString = `2px solid ${Theme.Color.varForeground}`;

/**
 *
 * @name MellowFrameHeaderStyle
 * @author Peter Laxalt
 *
 */
export const MellowFrameHeaderStyle = styled.div`
  &.${MellowFrameHeaderClassName} {
    // __________________________
    // Settings
    --${MellowFrameHeaderClassName}__frame-edge-size: ${Root.FrameSize};
    --${MellowFrameHeaderClassName}__frame-padding: calc(${Root.FrameSize} / 2);
    --${MellowFrameHeaderClassName}__marquee-speed: 60s;

    @media(max-width: ${Theme.Base.Media.Width.Md}) {
      --${MellowFrameHeaderClassName}__frame-edge-size: ${Root.FrameSize};
    }


    overflow: hidden;

    position: fixed;

    left: 0;
    top: 0;

    width: 100vw;
    height: 100vh;

    z-index: 900;

    pointer-events: none;

    transform: scale(1);
    transform-origin: center center;
    will-change: transform;
    transition: transform .5s ease-in-out;


    &.${MellowFrameHeaderClassName}--inactive,
    &.${MellowFrameHeaderClassName}--fill-bars-is-expanded {
      /* transform: scale(1.2); */
    }

    // __________________________
    // Inner Grid
    .${MellowFrameHeaderClassName}__inner {
      width: 100vw;
      height: 100vh;

      position: relative;

      z-index: 999;

      pointer-events: none;

      border: var(--${MellowFrameHeaderClassName}__frame-padding) solid ${Theme.Color.varBackground};

      display: flex;
    }

    // __________________________
    // Frame container
    .${MellowFrameHeaderClassName}__frame {
      border: ${CssFrameBorderString};
      width: 100%;

      z-index: 500;

      display: flex;
      flex-direction: column;
      justify-content: space-between;

      overflow: hidden;

      &__nav {
        width: 100%;
        height: ${Root.FrameSize};

        background: ${Theme.Color.varBackground};

        pointer-events: all;

        text-transform: uppercase;

        &--top {
          border-bottom: ${CssFrameBorderString};
        }

        &--bottom {
          border-top: ${CssFrameBorderString};
        }

        &__inner {
          display: flex;
          align-items: center;
        }

        &__col {
          width: 50%;
          height: ${Root.FrameSize};

          display: flex;
          align-items: center;

          &--time {
            flex: 1;
          }

          &--contact {
            width: auto;
            flex-shrink: 0;
          }

          &--left {
            padding-left: 1rem;
            padding-right: 1rem;
          }

          &--right {
            padding-right: 1rem;
          }
        }

        &__inner-col {
          width: 50%;

          &--misc {
            text-align: right;
            font-weight: 800;
          }

          &--contact-btn,
          &--theme-picker {
            width: auto;
            flex-shrink: 0;
          }

          &--contact-btn {
            font-weight: 800;
            padding-left: 1.5rem;
          }
        }

        &__misc-wrapper,
        &__linklist {
          position: relative;

          &:before {
            content: '';
            position: absolute;

            top: 50%;

            width: 1.5rem;
            height: 1px;

            background: ${Theme.Color.varForeground};

            transform: translateY(-50%);
          }
        }

        &__linklist {
          padding-left: 2.5rem;

          &:before {
            left: 0;
          }
        }

        &__misc-wrapper {
          padding-right: 2.5rem;
          display: flex;
          justify-content: flex-end;

          &:before {
            right: 0;
          }

          &__el {
            margin-right: 0 !important;
          }
        }

        &__brandmark {
          font-weight: 800;

          &__label {
            width: auto;
          }
        }

        &__linklist {
          display: flex;

          position: relative;

          &__item {
            margin-right: 2rem;

            &__el {
              color: ${Theme.Color.varForeground};

              height: ${Root.FrameSize};

              display: inline-flex;
              align-items: center;

              position: relative;

              overflow: hidden;

              &--contact-btn {
                &:before {
                  bottom: unset;
                  top: 0;
                }
              }

              &:before {
                content: '';
                position: absolute;

                left: 0;
                right: 0;
                bottom: 0;

                width: 100%;
                height: 3px;

                background: ${Theme.Color.varForeground};

                /* transform: translateY(100%);
                transition: transform .5s ease; */

                opacity: 0;
              }

              &:hover {
                text-decoration: none;

                &:before {
                  /* transform: translateY(0%); */
                  opacity: 1;
                }
              }
            }

            &__time {
              font-size: .6rem;
              font-weight: bold;
              text-transform: uppercase;
              letter-spacing: .1rem;

              line-height: 2.25;
            }
          }
        }
      }
    }

  }

`;

/**
 *
 * @name InteractiveLogotypeClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const InteractiveLogotypeClassName = "interactive-logotype";

/**
 *
 * @name InteractiveLogotypeStyle
 * @author Peter Laxalt
 *
 */
export const InteractiveLogotypeStyle = styled.div`
  &.${InteractiveLogotypeClassName} {
    position: fixed;

    top: 0;
    left: 0;

    width: 100vw;
    height: 100vh;

    overflow: hidden;

    pointer-events: none;

    z-index: 875;

    &--fill {
      z-index: 800;
    }

    &--fill {
      &.${InteractiveLogotypeClassName}--is-expanded {
        .${InteractiveLogotypeClassName}__inner {
          .${InteractiveLogotypeClassName}__el {
            &:before {
              transform: translate(-50%, -50%) scale(30);

              pointer-events: none;
            }
          }
        }
      }
    }

    .${InteractiveLogotypeClassName}__inner {
      position: absolute;

      top: 1rem;
      left: 50%;

      transform: translate(-50%, -150%);
      transition: transform 1s ease;
      will-change: transform;

      z-index: 800;

      max-width: 101px;

      .${InteractiveLogotypeClassName}__el {
        font-family: "dharma";
        font-size: calc(${Root.FrameSize} * 1.5);
        line-height: 0.8;

        display: block;

        padding: 1rem;

        color: ${Theme.Color.varBackground};

        pointer-events: all;

        &__label {
          position: relative;
          z-index: 5;
        }

        &:before,
        &:after {
          content: "";

          position: absolute;

          left: 50%;
          top: 50%;

          transform: translate(-50%, -50%) scale(1);
          transform-origin: center center;
          transition: transform 1.5s ease;
          will-change: transform;

          width: 100%;
          height: 100%;
        }

        &:before {
          background: ${Theme.Color.varForeground};
        }

        &:after {
          background: ${Theme.Color.varForeground};
          border: 2px solid ${Theme.Color.varForeground};
          box-shadow: 0px 0px 0px 2px ${Theme.Color.varBackground},
            0px 0px 0px 4px ${Theme.Color.varForeground};
        }

        &:hover {
          color: ${Theme.Color.varForeground};

          &:after {
            background: ${Theme.Color.varBackground};
            border: 2px solid ${Theme.Color.varForeground};
          }
        }
      }
    }

    &--inactive,
    &--fill-bars-is-expanded {
      .${InteractiveLogotypeClassName}__inner {
        transform: translate(-50%, 0%);
      }
    }

    @media (max-width: ${Theme.Base.Media.Width.Sm}) {
      .${InteractiveLogotypeClassName}__inner {
        transform: translate(-50%, 0%);
      }
    }
  }
`;

/**
 *
 * @name InteractiveFillBarsClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const InteractiveFillBarsClassName = "interactive-fill-bars";

/**
 *
 * @name InteractiveFillBarsStyle
 * @author Peter Laxalt
 *
 */
export const InteractiveFillBarsStyle = styled.div`
  &.${InteractiveFillBarsClassName} {
    // __________________________
    // Active state
    &.${InteractiveFillBarsClassName}--is-expanded {
      .${InteractiveFillBarsClassName}__fill-bar {
        &--top,
        &--bottom {
          transform: scaleY(1);
        }

        &--left,
        &--right {
          transform: scaleX(1);
        }
      }
    }

    // __________________________
    // Fill bars
    .${InteractiveFillBarsClassName}__fill-bar {
      background: ${Theme.Color.varForeground};

      will-change: transform;
      transition: transform 1.5s ease-in-out;

      display: block;

      position: fixed;

      z-index: 700;

      &--top,
      &--bottom {
        height: 60vh;
        width: 100vw;

        transform: scaleY(0);
        left: 0;
      }

      &--left,
      &--right {
        width: 60vw;
        height: 100vh;

        transform: scaleX(0);
        top: 0;
      }

      &--top {
        transform-origin: top center;
        top: 0;
      }

      &--bottom {
        transform-origin: bottom center;
        bottom: 0;
      }

      &--left {
        transform-origin: left center;
        left: 0;
      }

      &--right {
        transform-origin: right center;
        right: 0;
      }
    }
  }
`;
