// ____________________________________________________________________________________

export type LXLT_WaterTextureOptions = {
  debug?: boolean;
};

export type LXLT_WaterTexturePoint = {
  x: number;
  y: number;
  age?: number;
};

/**
 *
 * @name WaterTexture
 * @see https://tympanus.net/codrops/2019/10/08/creating-a-water-like-distortion-effect-with-three-js/
 *
 */

export class WaterTexture {
  // _________________________________
  // Types
  size: number;
  radius: number;
  width: number;
  height: number;
  canvas: HTMLCanvasElement;
  ctx: CanvasRenderingContext2D;
  debug?: boolean;
  points: LXLT_WaterTexturePoint[];
  maxAge: number;
  last: LXLT_WaterTexturePoint;

  // _________________________________
  // Constructor
  constructor(options: LXLT_WaterTextureOptions) {
    this.size = 64;
    this.radius = this.size * 0.1;
    this.width = this.height = this.size;

    this.size = 64;
    this.radius = this.size * 0.1;

    this.points = [];
    this.maxAge = 64;

    this.last = null;

    if (options.debug) {
      this.width = window.innerWidth;
      this.height = window.innerHeight;
      this.radius = this.width * 0.05;
    }

    this.initTexture();
    if (options.debug) document.body.append(this.canvas);
  }

  /**
   *
   * @description Initialize our canvas
   *
   */
  initTexture() {
    this.canvas = document.createElement("canvas");
    this.canvas.id = "WaterTexture";
    this.canvas.width = this.width;
    this.canvas.height = this.height;
    this.ctx = this.canvas.getContext("2d");
    this.clear();
  }

  /**
   *
   * @description Note that addPoint() receives normalized values, from 0 to 1.
   * If the canvas happens to resize, we can use the normalized points to draw using the correct size.
   *
   */

  addPoint(point: LXLT_WaterTexturePoint) {
    this.points.push({ x: point.x, y: point.y, age: 0 });
  }

  /**
   *
   * @description Letâ€™s create drawPoint(point) to start drawing the ripples:
   * Convert the normalized point coordinates into canvas coordinates. Then, draw a happy little circle.
   *
   */
  drawPoint(point: LXLT_WaterTexturePoint) {
    // Convert normalized position into canvas coordinates
    let pos = {
      x: point.x * this.width,
      y: point.y * this.height,
    };
    const radius = this.radius;

    this.ctx.beginPath();
    this.ctx.arc(pos.x, pos.y, radius, 0, Math.PI * 2);
    this.ctx.fill();

    const ctx = this.ctx;
    // Lower the opacity as it gets older
    let intensity = 1;
    intensity = 1 - point.age / this.maxAge;

    let color = "255,255,255";

    let offset = this.width * 5;
    // 1. Give the shadow a high offset.
    ctx.shadowOffsetX = offset;
    ctx.shadowOffsetY = offset;
    ctx.shadowBlur = radius * 1;
    ctx.shadowColor = `rgba(${color},${0.2 * intensity})`;

    this.ctx.beginPath();
    this.ctx.fillStyle = "rgba(255,0,0,1)";
    // 2. Move the circle to the other direction of the offset
    this.ctx.arc(pos.x - offset, pos.y - offset, radius, 0, Math.PI * 2);
    this.ctx.fill();
  }

  clear() {
    this.ctx.fillStyle = "black";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
  }

  update() {
    this.clear();
    this.points.forEach((point) => {
      point.age += 1;
      if (point.age > this.maxAge) {
        this.points.splice(i, 1);
      }
    });
    this.points.forEach((point) => {
      this.drawPoint(point);
    });
  }
}
