// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../../constants/Theme";
import {} from "../../../constants/Root";
import { DharmaTypeClassName } from "../../_svg/DharmaType/styles.scss";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name InteractiveFrameHeaderClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const InteractiveFrameHeaderClassName = "interactive-frame-header";

/**
 *
 * @name InteractiveFrameHeaderStyle
 * @author Peter Laxalt
 *
 */
export const InteractiveFrameHeaderStyle = styled.div`
  &.${InteractiveFrameHeaderClassName} {
    --frameEdgeSize: 3rem;

    .${InteractiveFrameHeaderClassName}__inner {
      width: 100vw;
      height: 100vh;

      position: relative;
    }

    .${DharmaTypeClassName}, .${InteractiveFrameHeaderClassName}__frame {
      position: absolute;

      top: 0;
      left: 0;
      right: 0;
      bottom: 0;

      width: 100%;
      height: 100%;
    }

    .${InteractiveFrameHeaderClassName}__type {
      position: absolute;

      top: 50%;
      left: 50%;

      transform: translate(-50%, -50%);

      fill: ${Theme.Color.Galaxy};

      width: calc(100% - (var(--frameEdgeSize) * 2));
      height: calc(100% - (var(--frameEdgeSize) * 2));

      .${DharmaTypeClassName} {
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;

        width: 100%;
        height: 100%;
      }
    }

    .${InteractiveFrameHeaderClassName}__frame {
      &__y-axis,
      &__x-axis {
        position: absolute;

        overflow: hidden;

        left: 50%;
        top: 50%;
      }

      &__y-axis {
        width: 100vw;
        height: 100vh;

        transform: translate(-50%, -50%);
      }

      &__x-axis {
        width: 100vh;
        height: 100vw;

        transform: translate(-50%, -50%) rotate(90deg);
      }

      &__edge {
        font-size: calc(var(--frameEdgeSize) * 0.75);
        text-transform: uppercase;
        line-height: 0;

        .${InteractiveFrameHeaderClassName}__frame-items {
          list-style-type: none;

          color: ${Theme.Color.Galaxy};

          height: var(--frameEdgeSize);

          display: block;
          align-items: center;
          justify-content: flex-start;

          li {
            margin-right: calc(var(--frameEdgeSize) / 2);
          }
        }

        .${InteractiveFrameHeaderClassName}__marquee-container {
          flex-shrink: 0;

          display: flex;
          justify-content: flex-start;

          flex-wrap: nowrap;
          position: relative;

          overflow: hidden;

          .${InteractiveFrameHeaderClassName}__frame-items {
            display: flex;
            flex-wrap: nowrap;
            white-space: nowrap;

            flex-shrink: 0;

            li {
              width: auto;
              flex-shrink: 0;
              white-space: nowrap;
            }
          }
        }

        &--top,
        &--bottom,
        &--left,
        &--right {
          position: absolute;

          overflow: hidden;
        }

        &--top,
        &--right {
          border-bottom: 1px solid ${Theme.Color.Galaxy};

          .${InteractiveFrameHeaderClassName}__frame-items {
            padding-top: calc(var(--frameEdgeSize) / 6);
            padding-bottom: calc(var(--frameEdgeSize) / 6);
          }
        }

        &--bottom,
        &--left {
          border-top: 1px solid ${Theme.Color.Galaxy};

          .${InteractiveFrameHeaderClassName}__frame-items {
            padding-top: calc(var(--frameEdgeSize) / 6);
            padding-bottom: calc(var(--frameEdgeSize) / 6);

            animation: marqueeBottomLeftQuadrant 60s linear infinite;
          }
        }

        &--top,
        &--bottom {
          width: calc(100vw - var(--frameEdgeSize));
        }

        &--left,
        &--right {
          width: calc(100vh - var(--frameEdgeSize));
        }

        &--top {
          /* background: seashell; */

          top: 0;
          left: var(--frameEdgeSize);
        }

        &--bottom {
          /* background: magenta; */

          bottom: 0;
          right: var(--frameEdgeSize);
        }

        &--right {
          /* background: orange; */

          top: 0;
          left: var(--frameEdgeSize);
        }

        &--left {
          /* background: goldenrod; */

          bottom: 0;
          left: 0;
        }
      }
    }
  }
`;
