// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../constants/Theme";
import { Root } from "../../constants/Root";
import { DharmaTypeClassName } from "../_svg/DharmaType/styles.scss";
import {} from "../../constants/styles/CssUtils";
import { ThemePickerClassName } from "../ThemePicker/styles.scss";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name MellowFrameHeaderClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const MellowFrameHeaderClassName = "mellow-frame-header";

/**
 *
 * @name MellowFrameHeaderStyle
 * @author Peter Laxalt
 *
 */
export const MellowFrameHeaderStyle = styled.div`
  &.${MellowFrameHeaderClassName} {
    // __________________________
    // Settings
    --${MellowFrameHeaderClassName}__frame-edge-size: ${Root.FrameSize};
    --${MellowFrameHeaderClassName}__frame-padding: calc(${Root.FrameSize} / 2);
    --${MellowFrameHeaderClassName}__marquee-speed: 60s;

    @media(max-width: ${Theme.Base.Media.Width.Md}) {
      --${MellowFrameHeaderClassName}__frame-edge-size: ${Root.FrameSize};
    }


    overflow: hidden;

    position: fixed;

    left: 0;
    top: 0;

    width: 100vw;
    height: 100vh;

    z-index: 900;

    pointer-events: none;

    transform: scale(1);
    transform-origin: center center;
    will-change: transform;
    transition: transform .5s ease-in-out;


    &.${MellowFrameHeaderClassName}--inactive,
    &.${MellowFrameHeaderClassName}--fill-bars-is-expanded {
      /* transform: scale(1.2); */
    }

    // __________________________
    // Inner Grid
    .${MellowFrameHeaderClassName}__inner {
      width: 100vw;
      height: 100vh;

      position: relative;

      z-index: 999;

      pointer-events: none;

      padding: var(--${MellowFrameHeaderClassName}__frame-padding);

      display: flex;
    }

    // __________________________
    // Frame container
    .${MellowFrameHeaderClassName}__frame {
      border: 1px solid ${Theme.Color.varForeground};
      width: 100%;

      z-index: 500;

      display: flex;
      flex-direction: column;
      justify-content: space-between;

      &__nav {
        width: 100%;
        height: ${Root.FrameSize};

        background: ${Theme.Color.varBackground};
      }
    }

  }

`;

/**
 *
 * @name InteractiveLogotypeClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const InteractiveLogotypeClassName = "interactive-logotype";

/**
 *
 * @name InteractiveLogotypeStyle
 * @author Peter Laxalt
 *
 */
export const InteractiveLogotypeStyle = styled.div`
  &.${InteractiveLogotypeClassName} {
    position: fixed;

    top: 0;
    left: 0;

    width: 100vw;
    height: 100vh;

    overflow: hidden;

    pointer-events: none;

    z-index: 875;

    &--fill {
      z-index: 800;
    }

    &--fill {
      &.${InteractiveLogotypeClassName}--is-expanded {
        .${InteractiveLogotypeClassName}__inner {
          .${InteractiveLogotypeClassName}__el {
            &:before {
              transform: translate(-50%, -50%) scale(30);

              pointer-events: none;
            }
          }
        }
      }
    }

    .${InteractiveLogotypeClassName}__inner {
      position: absolute;

      top: 1rem;
      left: 50%;

      transform: translate(-50%, -150%);
      transition: transform 1s ease;
      will-change: transform;

      z-index: 800;

      max-width: 101px;

      .${InteractiveLogotypeClassName}__el {
        font-family: "dharma";
        font-size: calc(${Root.FrameSize} * 1.5);
        line-height: 0.8;

        display: block;

        padding: 1rem;

        color: ${Theme.Color.varBackground};

        pointer-events: all;

        &__label {
          position: relative;
          z-index: 5;
        }

        &:before,
        &:after {
          content: "";

          position: absolute;

          left: 50%;
          top: 50%;

          transform: translate(-50%, -50%) scale(1);
          transform-origin: center center;
          transition: transform 1.5s ease;
          will-change: transform;

          width: 100%;
          height: 100%;
        }

        &:before {
          background: ${Theme.Color.varForeground};
        }

        &:after {
          background: ${Theme.Color.varForeground};
          border: 2px solid ${Theme.Color.varForeground};
          box-shadow: 0px 0px 0px 2px ${Theme.Color.varBackground},
            0px 0px 0px 4px ${Theme.Color.varForeground};
        }

        &:hover {
          color: ${Theme.Color.varForeground};

          &:after {
            background: ${Theme.Color.varBackground};
            border: 2px solid ${Theme.Color.varForeground};
          }
        }
      }
    }

    &--inactive,
    &--fill-bars-is-expanded {
      .${InteractiveLogotypeClassName}__inner {
        transform: translate(-50%, 0%);
      }
    }

    @media (max-width: ${Theme.Base.Media.Width.Sm}) {
      .${InteractiveLogotypeClassName}__inner {
        transform: translate(-50%, 0%);
      }
    }
  }
`;

/**
 *
 * @name InteractiveFillBarsClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const InteractiveFillBarsClassName = "interactive-fill-bars";

/**
 *
 * @name InteractiveFillBarsStyle
 * @author Peter Laxalt
 *
 */
export const InteractiveFillBarsStyle = styled.div`
  &.${InteractiveFillBarsClassName} {
    // __________________________
    // Active state
    &.${InteractiveFillBarsClassName}--is-expanded {
      .${InteractiveFillBarsClassName}__fill-bar {
        &--top,
        &--bottom {
          transform: scaleY(1);
        }

        &--left,
        &--right {
          transform: scaleX(1);
        }
      }
    }

    // __________________________
    // Fill bars
    .${InteractiveFillBarsClassName}__fill-bar {
      background: ${Theme.Color.varForeground};

      will-change: transform;
      transition: transform 1.5s ease-in-out;

      display: block;

      position: fixed;

      z-index: 700;

      &--top,
      &--bottom {
        height: 60vh;
        width: 100vw;

        transform: scaleY(0);
        left: 0;
      }

      &--left,
      &--right {
        width: 60vw;
        height: 100vh;

        transform: scaleX(0);
        top: 0;
      }

      &--top {
        transform-origin: top center;
        top: 0;
      }

      &--bottom {
        transform-origin: bottom center;
        bottom: 0;
      }

      &--left {
        transform-origin: left center;
        left: 0;
      }

      &--right {
        transform-origin: right center;
        right: 0;
      }
    }
  }
`;
