// Core
import React from "react";

// Constants
import { __DEBUG__ } from "../../constants/site/Settings";

// Types
import { LayeredSidebar, LXLT_LayeredSidebar } from "../LayeredSidebar";

// Components

// Styles
import {
  SimpleContentTemplateClassName,
  SimpleContentTemplateStyle,
} from "./styles.scss";

// Begin Types
// __________________________________________________________________________________________

export type LXLT_SimpleContentTemplate = {
  addClass?: string;
  sidebar?: LXLT_LayeredSidebar;
  children: React.ReactNode;
};

// Begin Data
// __________________________________________________________________________________________

export const SimpleContentTemplate: React.FunctionComponent<LXLT_SimpleContentTemplate> = ({
  addClass,
  children,
  sidebar,
}) => {
  return (
    <SimpleContentTemplateStyle
      className={`${SimpleContentTemplateClassName} ${
        addClass && addClass
      } ${SimpleContentTemplateClassName}--${
        sidebar ? `has-siderbar` : `sans-sidebar`
      }`}
    >
      <div className={`${SimpleContentTemplateClassName}__inner`}>
        {/* _____________________________________________________________ */}
        {/* Sidebar */}
        {sidebar ? (
          <LayeredSidebar
            addClass={`${SimpleContentTemplateClassName}__sidebar`}
          />
        ) : null}

        {/* _____________________________________________________________ */}
        {/* Content */}
        <div className={`${SimpleContentTemplateClassName}__content-wrapper`}>
          {children}
        </div>
      </div>
    </SimpleContentTemplateStyle>
  );
};

// End Component
// ______________________________________________________________________________
