/**
 *
 * Cursor.js
 * @author Peter Laxalt
 * @description The website Cursor.
 *
 */

// Core
import React, { Component } from "react";

// Styles
import { CursorStyle } from "./styles.scss";

// Begin Component
//////////////////////////////////////////////////////////////////////

// Cursor
export class Cursor extends Component<{}, any> {
  constructor(props: any) {
    super(props);

    this.state = {
      mouseX: 0,
      mouseY: 0,
      xPos: 0,
      yPos: 0,
      dx: 0,
      dy: 0,
      mouseSize: 20
    };
  }

  componentDidMount() {
    /**
     *
     * @name Set Mouse Position
     * @param e : Event from "mousemove" event listener.
     * @description This positions the mouse throughout the page.
     *
     */
    const setMousePosition = (e: any) => {
      let xPos = this.state.mouseX - this.state.mouseSize / 2;
      let yPos = this.state.mouseY - this.state.mouseSize / 2;

      let dX = this.state.mouseX - this.state.xPos;
      let dY = this.state.mouseY - this.state.yPos;

      this.setState({
        xPos: xPos + dX / 10,
        yPos: yPos + dY / 10,
        mouseX: e.clientX,
        mouseY: e.clientY
      });

      console.log("x: " + this.state.mouseX, "y: " + this.state.mouseY);
    };

    /**
     *
     * @name Expand Mouse
     * @param e : Event from "mousedown" event listener.
     * @description This positions the mouse throughout the page.
     *
     */
    const expandMouse = (e: any) => {
      console.log(e);
    };

    document.addEventListener("mousemove", setMousePosition, false);
    document.addEventListener("mousedown", expandMouse, false);
  }

  render() {
    return (
      <CursorStyle
        className="cursor"
        style={{
          transform:
            "translate(" + this.state.xPos + "px," + this.state.yPos + "px)",
          width: this.state.mouseSize,
          height: this.state.mouseSize
        }}
      >
        <div className="cursor-inner" />
      </CursorStyle>
    );
  }
}
