// Core
import React from "react";

// Begin Types
// __________________________________________________________________________________________

type LXLT_DharmaType = {
  text: string;
};

// Begin Component
// __________________________________________________________________________________________

export const DharmaTypeClassName = "dharma-laxalt";

/**
 * @name DharmaType
 * @author Peter Laxalt
 *
 */
export const DharmaType: React.FunctionComponent<LXLT_DharmaType> = ({
  text,
}) => {
  let characterArray = text.split("");

  console.log(characterArray);

  return (
    <svg
      xmlns="http://www.w3.org/2000/svg"
      viewBox="0 0 215.21 173.21"
      preserveAspectRatio="none"
      className={`${DharmaTypeClassName}`}
    >
      <defs>
        <style
          dangerouslySetInnerHTML={{
            __html: `
          .${DharmaTypeClassName}__text__char {
            font-size: 173.56px;
            font-family: DharmaGothicC-ExBold, Dharma Gothic C;
            font-weight: 700;
            letter-spacing: 0em;
          }`,
          }}
        />
      </defs>
      <g>
        <g>
          <text
            className={`${DharmaTypeClassName}__text`}
            transform="translate(0 170.53) scale(1.15, 1.39)"
          >
            {text}
          </text>
        </g>
      </g>
    </svg>
  );
};

