import * as THREE from "three";
import { __DEBUG__ } from "../../constants/site/Settings";
import { LXLT_ColorTheme } from "../../constants/styles/Color";
import { EffectComposer } from "./jsm/postprocessing/EffectComposer";
import { RenderPass } from "./jsm/postprocessing/RenderPass";
import {
  LXLT_WaterTexturePoint,
  WaterTexture,
} from "./water-distort/WaterTexture";

// Begin Component
// __________________________________________________________________________

export type LXLT_ThreeWaterCanvas = {
  container: HTMLDivElement;
  CanvasElement: HTMLCanvasElement;
  CanvasTheme: LXLT_ColorTheme;
};

export class ThreeWaterCanvasClass {
  // ______________________________
  // Types
  waterTexture: WaterTexture;
  renderer: THREE.WebGLRenderer;
  camera: THREE.PerspectiveCamera;
  scene: THREE.Scene;
  composer: EffectComposer;
  clock: THREE.Clock;

  // ______________________________
  // Constructor
  constructor(props: LXLT_ThreeWaterCanvas) {
    this.waterTexture = new WaterTexture({ debug: true });

    this.renderer = new THREE.WebGLRenderer({
      antialias: false,
    });

    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.renderer.setPixelRatio(window.devicePixelRatio);

    props.container.append(this.renderer.domElement);

    this.camera = new THREE.PerspectiveCamera(
      45,
      window.innerWidth / window.innerHeight,
      0.1,
      10000
    );
    this.camera.position.z = 50;

    this.tick = this.tick.bind(this);
    this.onMouseMove = this.onMouseMove.bind(this);

    // Post Processing
    this.composer = new EffectComposer(this.renderer);
    this.clock = new THREE.Clock();

    this.init();
  }

  initComposer() {
    const renderPass = new RenderPass(this.scene, this.camera);

    this.composer.addPass(renderPass);
  }

  addPlane() {
    let geometry = new THREE.PlaneBufferGeometry(5, 5, 1, 1);
    let material = new THREE.MeshNormalMaterial();
    let mesh = new THREE.Mesh(geometry, material);

    window.addEventListener("mousemove", this.onMouseMove);
    this.scene.add(mesh);
  }

  init() {
    this.initComposer();
    this.addPlane();
    this.tick();
  }

  onMouseMove(ev: MouseEvent) {
    const point: LXLT_WaterTexturePoint = {
      x: ev.clientX / window.innerWidth,
      y: ev.clientY / window.innerHeight,
    };
    this.waterTexture.addPoint(point);
  }

  render() {
    this.composer.render(this.clock.getDelta());
  }

  tick() {
    this.render();
    this.waterTexture.update();
    requestAnimationFrame(this.tick);
  }
}

const ThreeWaterCanvas = new ThreeWaterCanvasClass();

// ______________________________________________________________________________________________________________

export type LXLT_ThreeCanvasDisplay = (
  container: HTMLDivElement,
  CanvasElement: HTMLCanvasElement,
  CanvasTheme: LXLT_ColorTheme
) => void;

export const ThreeCanvas: LXLT_ThreeCanvasDisplay = (
  container,
  CanvasElement,
  CanvasTheme
) => {
  __DEBUG__ && console.log("ðŸ¤™ Three.js Canvas received:", CanvasElement);

  let camera, scene, renderer, mesh, material, sketch;

  init();
  setupCanvasDrawing();
  animate();

  function init() {
    camera = new THREE.PerspectiveCamera(
      50,
      window.innerWidth / window.innerHeight,
      1,
      6000
    );
    camera.position.z = 500;

    sketch = container;

    scene = new THREE.Scene();

    material = new THREE.MeshBasicMaterial();

    mesh = new THREE.Mesh(new THREE.BoxBufferGeometry(350, 350, 350), material);
    scene.add(mesh);

    renderer = new THREE.WebGLRenderer({ antialias: false, alpha: true });
    renderer.setPixelRatio(window.devicePixelRatio);
    renderer.setSize(window.innerWidth, window.innerHeight);
    sketch.appendChild(renderer.domElement);

    scene.background = new THREE.Color(0x000000);

    window.addEventListener("resize", onWindowResize, false);
  }

  function setupCanvasDrawing() {
    material.map = new THREE.CanvasTexture(CanvasElement);
  }

  function onWindowResize() {
    camera.aspect = window.innerWidth / window.innerHeight;
    camera.updateProjectionMatrix();

    renderer.setSize(window.innerWidth, window.innerHeight);
  }

  function animate() {
    requestAnimationFrame(animate);

    mesh.rotation.x += 0.01;
    mesh.rotation.y += 0.01;

    material.map.needsUpdate = true;

    renderer.render(scene, camera);
  }
};
