// Core
import React from "react";
import { createGlobalStyle } from "styled-components";
import { CssUtils } from "../../constants/styles/CssUtils";
import { Theme } from "../../constants/Theme";
import { ThemePickerClassName, ThemePickerStyle } from "./styles.scss";

// Begin Types
// __________________________________________________________________________________________

type LXLT_ThemePicker = {};

type LXLT_Theme = {
  name: string;
  primary: string;
  secondary: string;
  background: string;
  foreground: string;
};

type LXLT_ThemePickerState = {
  themes: LXLT_Theme[];
  activeTheme?: LXLT_Theme;
};

// Begin Component
// __________________________________________________________________________________________

/**
 * @name ThemePicker
 * @author Peter Laxalt
 *
 */
export class ThemePicker extends React.PureComponent<
  LXLT_ThemePicker,
  LXLT_ThemePickerState
> {
  constructor(props: LXLT_ThemePicker) {
    super(props);

    this.state = {
      themes: [
        {
          name: "lightcyan",
          primary: "red",
          secondary: "red",
          background: "lightcyan",
          foreground: "red",
        },
        {
          name: "goldenrod",
          primary: "red",
          secondary: "red",
          background: "goldenrod",
          foreground: "red",
        },
        {
          name: "honeydew",
          primary: "red",
          secondary: "red",
          background: "honeydew",
          foreground: "red",
        },
      ],
    };

    // this.letterCountTimer = this.letterCountTimer.bind(this);
  }

  componentDidMount() {}

  incrementLetterCount(id: number) {
    // this.setState({
    //   characters: this.state.characters.map(
    //     (character: LXLT_DharmaChar, idx: number) => {
    //       return {
    //         letter: character.letter,
    //         count: id == character.idx ? character.count + 1 : character.count,
    //         idx: idx,
    //       };
    //     }
    //   ),
    // });
  }

  render() {
    let { activeTheme } = this.state;

    const SetGlobalTheme = createGlobalStyle`
      ${
        activeTheme
          ? CssUtils.CreateTheme(
              activeTheme.primary,
              activeTheme.secondary,
              activeTheme.background,
              activeTheme.foreground
            )
          : ""
      }
    `;

    return (
      <>
        <SetGlobalTheme />
        <ThemePickerStyle className={`${ThemePickerClassName}`}>
          PICKER
        </ThemePickerStyle>
      </>
    );
  }
}
