// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../../constants/Theme";
import { Root } from "../../../constants/Root";
import {
  CssFramePaddingString,
  CssFrameSizeWithBorderString,
} from "../../MellowFrameHeader/styles.scss";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name ProjectScrollSectionClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const ProjectScrollSectionClassName = "project-scroll";

/**
 *
 * @name ProjectScrollSectionStyle
 * @author Peter Laxalt
 *
 */
export const ProjectScrollSectionStyle = styled.section`
  &.${ProjectScrollSectionClassName} {
    .${ProjectScrollSectionClassName}__sticky-wrapper {
      position: sticky;

      top: 0;

      height: 100vh;
      width: 100%;

      overflow-x: hidden;
    }

    .${ProjectScrollSectionClassName}__content-wrapper {
      position: relative;

      height: 100vh;

      padding: 0;

      display: flex;

      flex-flow: row nowrap;
      justify-content: flex-start;
      align-items: center;

      padding-left: 100vw;
      padding-right: 100vw;
    }

    .${ProjectScrollSectionClassName}__horizontal-wrapper {
      position: relative;

      width: 100%;
      min-height: 100vh;
    }

    .${ProjectScrollSectionClassName}__x-translate-wrapper {
      position: absolute;

      height: 100%;

      /* transition: transform 0.5s ease-out; */
      will-change: transform;
    }

    .${ProjectScrollSectionClassName}__outer {
      position: relative;

      width: 100%;
    }
  }
`;

/**
 *
 * @name ScrollCard
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const ScrollCardClassName = "scroll-card";

export const ScrollCardStyle = styled.div`
  &.${ScrollCardClassName} {
    height: 100vh;
    width: 50vw;

    padding: calc(${Root.FrameSize} + (${Root.FrameSize} / 2)) calc(${Root.Size} * 2) calc(${Root.FrameSize} / 2)
      calc(${Root.Size} * 2);

    display: flex;
    align-items: flex-end;

    flex-shrink: 0;

    position: relative;

    // Borders
    &:before,
    &:after {
      content: "";

      position: absolute;



      height: 100%;
      width: 1px;

      background: linear-gradient(
        to bottom,
        rgba(0,0,0, 0),
        rgba(0,0,0, 0) 50%,
        ${Theme.Color.varForeground} 50%,
        ${Theme.Color.varForeground}
      );

      background-size: 100% 6px;
    }

    // Right Border
    &:after {
      right: 0;
      top: 0;
      bottom: 0;
    }

    // Center Border
    &:before {
      left: 50%;
      top: 0;
      bottom: 0;

      transform: translateX(-50%);
    }


    img {
      width: 100%;
      max-width: 100%;
    }

    .${ScrollCardClassName}__inner {
      height: 100%;
      width: 100%;

      position: relative;
      z-index: 2;

      display: flex;
      justify-content: space-between;
      flex-direction: column;
    }

    .${ScrollCardClassName}__row {
      display: flex;
      justify-content: space-between;

      &--top {
        padding-top: calc(${Root.FrameSize} / 4);
      }

      &--bottom {
        padding-bottom: calc(${Root.FrameSize} / 4);
      }
    }

    .${ScrollCardClassName}__image-wrapper {
      background: ${Theme.Color.varForeground};

      line-height: 0;

      position: relative;

      .${ScrollCardClassName}__image {

        &--filtered {
          mix-blend-mode: screen;
        }

        &--normal {
          position: absolute;
          left: 0;
          top: 0;

          opacity: 0;
        }
      }
    }
  }
`;
