/**
 *
 * Three Entry Point
 * @author Pierfrancesco Soffritti
 * @url (https://itnext.io/how-to-use-plain-three-js-in-your-react-apps-417a79d926e0)
 * @description Three entry point for coding & embedding three.js apps normally.
 *
 */

// Components
import SceneManager from "../../sketches/three/t1";

// Begin Component
//////////////////////////////////////////////////////////////////////

export const ThreeEntryPoint = (containerElement: any) => {
  const canvas = createCanvas(document, containerElement);
  // @ts-ignore
  const sceneManager = new SceneManager(canvas);

  bindEventListeners();
  render();

  function createCanvas(document: any, containerElement: any) {
    const canvas = document.createElement("canvas");
    containerElement.appendChild(canvas);
    return canvas;
  }
  function bindEventListeners() {
    window.onresize = resizeCanvas;
    resizeCanvas();
  }
  function resizeCanvas() {
    canvas.style.width = "100%";
    canvas.style.height = "100%";
    canvas.width = canvas.offsetWidth;
    canvas.height = canvas.offsetHeight;
    sceneManager.onWindowResize();
  }
  // @ts-ignore
  function render(time?: any) {
    requestAnimationFrame(render);
    sceneManager.update();
  }
};
