// Core
import * as THREE from "three";

// Settings
import { __DEBUG__ } from "../../constants/site/Settings";

// Types
import { LXLT_ColorTheme } from "../../constants/styles/Color";

// Textures
import { EffectComposer } from "./jsm/postprocessing/EffectComposer";
import { RenderPass } from "./jsm/postprocessing/RenderPass";

// Textures
import {
  LXLT_WaterTexturePoint,
  WaterTexture,
} from "./water-distort/WaterTexture";

// Begin Component
// __________________________________________________________________________

export type LXLT_ThreeWaterCanvas = {
  canvasContainer: HTMLDivElement;
  canvasElement: HTMLCanvasElement;
  canvasTheme: LXLT_ColorTheme;
};

export class ThreeWaterCanvasClass {
  // ______________________________
  // Types
  waterTexture: WaterTexture;
  renderer: THREE.WebGLRenderer;
  camera: THREE.PerspectiveCamera;
  scene: THREE.Scene;
  composer: EffectComposer;
  clock: THREE.Clock;

  // ______________________________
  // Constructor
  constructor(options: LXLT_ThreeWaterCanvas) {
    this.waterTexture = new WaterTexture({ debug: true });

    this.renderer = new THREE.WebGLRenderer({
      antialias: false,
    });

    this.renderer.setSize(window.innerWidth, window.innerHeight);
    this.renderer.setPixelRatio(window.devicePixelRatio);

    options.canvasContainer.append(this.renderer.domElement);

    this.camera = new THREE.PerspectiveCamera(
      45,
      window.innerWidth / window.innerHeight,
      0.1,
      10000
    );
    this.camera.position.z = 50;

    this.tick = this.tick.bind(this);
    this.onMouseMove = this.onMouseMove.bind(this);

    // Post Processing
    this.composer = new EffectComposer(this.renderer);
    this.clock = new THREE.Clock();

    this.init();
  }

  initComposer() {
    const renderPass = new RenderPass(this.scene, this.camera);

    this.composer.addPass(renderPass);
  }

  addPlane() {
    let geometry = new THREE.PlaneBufferGeometry(5, 5, 1, 1);
    let material = new THREE.MeshNormalMaterial();
    let mesh = new THREE.Mesh(geometry, material);

    window.addEventListener("mousemove", this.onMouseMove);
    this.scene.add(mesh);
  }

  init() {
    this.initComposer();
    this.addPlane();
    this.tick();
  }

  onMouseMove(ev: MouseEvent) {
    const point: LXLT_WaterTexturePoint = {
      x: ev.clientX / window.innerWidth,
      y: ev.clientY / window.innerHeight,
    };
    this.waterTexture.addPoint(point);
  }

  render() {
    this.composer.render(this.clock.getDelta());
  }

  tick() {
    this.render();
    this.waterTexture.update();
    requestAnimationFrame(this.tick);
  }
}

export const ThreeWaterCanvas = (
  canvasContainer,
  canvasElement,
  canvasTheme
): ThreeWaterCanvasClass => {
  const myWaterCanvas = new ThreeWaterCanvasClass({
    canvasContainer,
    canvasElement,
    canvasTheme,
  });

  return myWaterCanvas;
};

