// Core
import React, { Ref } from "react";

// Styles
import { MainCanvasClassName, MainCanvasStyle } from "./styles.scss";

// Begin Types
// __________________________________________________________________________________________

type LXLT_MainCanvas = {};
type LXLT_MainCanvas_State = {};

// Begin Component
// _________________________________________________________________________________________

/**
 *
 * @name MainCanvas
 * @author Peter Laxalt
 *
 */
export class MainCanvas extends React.PureComponent<
  LXLT_MainCanvas,
  LXLT_MainCanvas_State
> {
  constructor(props: LXLT_MainCanvas) {
    super(props);

    this.state = {};

    this.canvasRef = React.createRef();
  }

  canvasRef = React.createRef<HTMLDivElement>();

  render() {
    return (
      <MainCanvasStyle className={`${MainCanvasClassName}`}>
        {/* ______________________________________________ */}
        {/* Inner Grid */}
        <div className={`${MainCanvasClassName}__inner`}>
          {/* ______________________________________________ */}
          {/* Canvas */}
          <div ref={this.canvasRef} />
        </div>
      </MainCanvasStyle>
    );
  }
}

// End Component
// ______________________________________________________________________________
