/**
 *
 * Navigation.js
 * @author Peter Laxalt
 * @description The website Navigation.
 *
 */

// Core
import React from "react";
import Link from "next/link";

// Styles
import { ListingsStyle, ListingStyle } from "./styles.scss";

// Constants
import { Theme } from "../../constants/Theme";

// Begin Interface
//////////////////////////////////////////////////////////////////////

interface Listing {
  name: string;
  href: string;
  theme: string;
  // width: string;
  thumb: string;
}

// Begin Component
//////////////////////////////////////////////////////////////////////

const projectsPrefix = "/projects";

const projects = [
  {
    name: "Test",
    href: projectsPrefix + "/test",
    theme: Theme.Color.Primary,
    width: "40%",
    thumb: projectsPrefix + "/1.png"
  },
  {
    name: "Test",
    href: projectsPrefix + "/test",
    theme: Theme.Color.Warning,
    width: "60%",
    thumb: projectsPrefix + "/2.png"
  },
  {
    name: "Test",
    href: projectsPrefix + "/test",
    theme: Theme.Color.Ocean,
    width: "50%",
    thumb: projectsPrefix + "/3.png"
  },
  {
    name: "Test",
    href: projectsPrefix + "/test",
    theme: Theme.Color.Dino,
    width: "50%",
    thumb: projectsPrefix + "/4.png"
  },
  {
    name: "Test",
    href: projectsPrefix + "/test",
    theme: Theme.Color.Cream,
    width: "1",
    thumb: projectsPrefix + "/1.png"
  },
  {
    name: "Test",
    href: projectsPrefix + "/test",
    theme: Theme.Color.Cream,
    width: "30%",
    thumb: projectsPrefix + "/2.png"
  }
];

const Listing = ({ name, href, theme, thumb }: Listing) => (
  <Link href={href}>
    <ListingStyle className="listing" theme={theme}>
      <div className="listing-inner">
        <span>{name}</span>
        <img src={thumb} alt={name} />
      </div>
    </ListingStyle>
  </Link>
);

export const Listings = () => (
  <ListingsStyle>
    <div className="inner">
      {projects.map((project, idx) => {
        return (
          <Listing
            name={project.name}
            href={project.href}
            theme={project.theme}
            // width={project.width}
            thumb={project.thumb}
            key={idx}
          />
        );
      })}
    </div>
  </ListingsStyle>
);

// End Component
//////////////////////////////////////////////////////////////////////
