import React, { PureComponent } from "react";
import { Layout } from "../../components/Layout/Layout";
import Sketch from "react-p5";

type TestState = {
  expanded: boolean;
  setup: any;
  draw: any;
};

class Test extends PureComponent<{}, TestState> {
  componentDidMount() {
    if (typeof window !== "undefined") {
      let x = 0;
      let scale = 50;
      let initialScale = 1500;
      let canvasX = window.innerWidth;
      let canvasY = window.innerHeight;

      let color = {
        primary: "rgba(0, 0, 0, .3)",
        primaryAlpha: "rgba(0, 0, 0, 1)",
        secondary: "#000000",
        bg: "#ffffff",
        white: "#ffffff"
      };

      const setup = (p5: any, parent: any) => {
        // Our Canvas
        let sketchCanvas = p5.createCanvas(p5.windowWidth, p5.windowHeight);
        sketchCanvas.parent("sketch");
      };

      const draw = (p5: any) => {
        scale += 1;
        x = x * 1.1 + 2;

        p5.noFill();
        p5.stroke(color.primary);

        for (let i = -1500; i < 10; i++) {
          p5.ellipse(
            i * 200,
            p5.windowHeight / 2,
            Math.sin(scale) * (Math.sin(scale) * initialScale),
            Math.sin(scale) * (Math.sin(scale) * initialScale)
          );
          p5.strokeWeight(0.5);
        }
      };
    }
  }

  render() {
    return (
      <Layout>
        <h1>Test Hello </h1>
        <Sketch setup={this.setup} draw={this.draw} />
      </Layout>
    );
  }
}

export default Test;
