import React, { PureComponent } from "react";
import { Layout } from "../../components/Layout/Layout";
import Sketch from "react-p5";

type TestState = {
  expanded: boolean;
  setup: any;
  draw: any;
};

class Test extends PureComponent<{}, TestState> {
  x = 0;
  scale = 50;
  initialScale = 1500;
  canvasX = window.innerWidth;
  canvasY = window.innerHeight;

  color = {
    primary: "rgba(0, 0, 0, .3)",
    primaryAlpha: "rgba(0, 0, 0, 1)",
    secondary: "#000000",
    bg: "#ffffff",
    white: "#ffffff"
  };

  setup = (p5: any, parent: any) => {
    // Our Canvas
    p5.createCanvas(p5.windowWidth, p5.windowHeight).parent(parent);
  };

  draw = (p5: any) => {
    this.scale += 1;
    this.x = this.x * 1.1 + 2;

    p5.noFill();
    p5.stroke(this.color.primary);

    for (let i = -1500; i < 10; i++) {
      p5.ellipse(
        i * 200,
        p5.windowHeight / 2,
        Math.sin(this.scale) * (Math.sin(this.scale) * this.initialScale),
        Math.sin(this.scale) * (Math.sin(this.scale) * this.initialScale)
      );
      p5.strokeWeight(0.5);
    }
  };

  render() {
    return (
      <Layout>
        <h1>Test Hello </h1>
        <Sketch setup={this.setup} draw={this.draw} />
      </Layout>
    );
  }
}

export default Test;
