// Core
import React, { useEffect, useRef, useState } from "react";
import styled from "styled-components";
import { LXLT_Project, SiteProjects } from "../../../constants/site/Settings";
import { Theme } from "../../../constants/Theme";

// Components

// Styles
import {
  ProjectScrollSectionClassName,
  ProjectScrollSectionStyle,
} from "./styles.scss";

// Begin Types
// __________________________________________________________________________________________

// Begin Component
// __________________________________________________________________________________________

// @ts-ignore
const ProjectScrollTallOuterContainerStyle = styled.div.attrs(({ dynamicHeight }) => ({
  style: { height: `${dynamicHeight}px` },
}))`
  position: relative;
  width: 100%;
`;

const ProjectScrollStickyInnerContainerStyle = styled.div`
  position: sticky;
  top: 0;
  height: 100vh;
  width: 100%;
  overflow-x: hidden;
`;

// @ts-ignore
const ProjectScrollHorizontalTranslateContainerStyle = styled.div.attrs(({ translateX }) => ({
  style: { transform: `translateX(${translateX}px)` },
}))`
  position: absolute;
  height: 100%;
  transition: transform 0.5s ease-out;
  will-change: transform;
`;

const ProjectScrollContentWrapperStyle = styled.div`
  position: relative;
  height: 100vh;
  padding: 0;
  display: flex;
  flex-flow: row nowrap;
  justify-content: flex-start;
  align-items: center;
`;

const ProjectScrollHorizontalSectionStyle = styled.section`
  position: relative;
  width: 100%;
  min-height: 100vh;
`;

const calcDynamicHeight = (objectWidth: number) => {
  const vw = window.innerWidth;
  const vh = window.innerHeight;
  return objectWidth - vw + vh + 150;
};

const handleDynamicHeight = (
  ref: React.RefObject<HTMLDivElement>,
  setDynamicHeight: (dynamicHeight: number) => void
) => {
  const objectWidth: number = ref.current.scrollWidth;
  const dynamicHeight: number = calcDynamicHeight(objectWidth);
  setDynamicHeight(dynamicHeight);
};

const applyScrollListener = (
  ref: React.RefObject<HTMLDivElement>,
  setTranslateX: (translateX: number) => void
) => {
  window.addEventListener("scroll", () => {
    const offsetTop: number = ref && ref.current ? -ref.current.offsetTop : 0;
    setTranslateX(offsetTop);
  });
};

export const ProjectScrollSection = ({
  addClass = "",
  hasSidebar = false,
  style = {},
}) => {
  const [dynamicHeight, setDynamicHeight] = useState(null);
  const [translateX, setTranslateX] = useState(0);

  const containerRef = React.useRef<HTMLDivElement>();
  const objectRef = React.useRef<HTMLDivElement>();

  const resizeHandler = () => {
    handleDynamicHeight(objectRef, setDynamicHeight);
  };

  useEffect(() => {
    handleDynamicHeight(objectRef, setDynamicHeight);
    window.addEventListener("resize", resizeHandler);
    applyScrollListener(containerRef, setTranslateX);
  }, []);

  return (
    <ProjectScrollSectionStyle
      style={style}
      className={`${ProjectScrollSectionClassName} ${ProjectScrollSectionClassName}--${
        hasSidebar ? "has-sidebar" : ""
      } ${addClass && addClass}`}
    >
      <ProjectScrollTallOuterContainerStyle
        // @ts-ignore
        dynamicHeight={dynamicHeight}
      >
        <ProjectScrollStickyInnerContainerStyle ref={containerRef}>
          <ProjectScrollHorizontalTranslateContainerStyle
            // @ts-ignore
            translateX={translateX}
            ref={objectRef}
          >
            <ProjectScrollHorizontalSectionStyle>
              <ProjectScrollContentWrapperStyle>
                <div style={{ width: "100vw" }} />
                {SiteProjects.map((item: LXLT_Project, idx: number) => {
                  return (
                    <div
                      key={idx}
                      style={{
                        width: "50vw",
                        flexShrink: 0,
                        marginRight: "50px",
                        position: "relative",
                        mixBlendMode: "multiply",
                      }}
                    >
                      <span
                        style={{
                          width: "100%",
                          height: "100%",
                          display: "grid",
                          mixBlendMode: "multiply",
                          placeItems: "center",
                        }}
                      >
                        <img
                          src={item.cover}
                          style={{
                            border: `2px solid ${Theme.Color.varBackground}`,
                            maxWidth: "100%",
                            width: "100%",
                            mixBlendMode: "multiply",
                          }}
                        />
                      </span>
                    </div>
                  );
                })}
                <div style={{ width: "100vw" }} />
              </ProjectScrollContentWrapperStyle>
            </ProjectScrollHorizontalSectionStyle>
          </ProjectScrollHorizontalTranslateContainerStyle>
        </ProjectScrollStickyInnerContainerStyle>
      </ProjectScrollTallOuterContainerStyle>
    </ProjectScrollSectionStyle>
  );
};

// End Component
// ______________________________________________________________________________
