/**
 *
 * index.js
 * @author Peter Laxalt
 * @description The website home page.
 *
 */

// Core
import { NextPage } from "next";
import React from "react";
import { Root } from "../constants/Root";

// Components
import WaterDistortWithCanvasTexturePage from "./projects/water-distort-with-canvas";

// Begin Component
// __________________________________________________________________________________________

type LMNTS_LocationsFrontPage = {};

export const HomePageClassName = "route__home-page";

/**
 *
 * pages/index.tsx
 * @author Peter Laxalt
 *
 */
const HomePage: NextPage<LMNTS_LocationsFrontPage> = () => {
  let num = 100000000000000000000000000000000;
  var arr = num.toString(10).replace(/\D/g, "0").split("").map(Number);

  return (
    <div className={`${HomePageClassName}`} style={{ minHeight: "200vh" }}>
      {/* <div style={{ height: "100vh", display: "grid", placeItems: "center" }}>
        <h1 style={{ fontFamily: "Hobo STD" }}>HOME PAGE</h1>
      </div> */}

      <div>
        <div style={{ position: "sticky", top: "0" }}>
          <WaterDistortWithCanvasTexturePage />
        </div>

        <ul
          style={{
            display: "flex",
            flexWrap: "wrap",
            justifyContent: "space-around",
          }}
        >
          {arr.map((i: any, idx: number) => {
            return (
              <li
                key={idx}
                style={{
                  width: "40%",
                  paddingBottom: "50%",
                  background: "green",
                  position: "relative",
                  marginBottom: "50px"
                }}
              >
                <span style={{ position: "absolute", top: "50%", left: "50%", transform: "translate(-50%, -50%)", color: "white" }}>
                  THING
                </span>
              </li>
            );
          })}
        </ul>
      </div>

      <div style={{ background: "yellow", height: "200vh" }} />
    </div>
  );
};

export default HomePage;
