// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import {} from "../../constants/Theme";
import {} from "../../constants/Root";
import {
  CssFrameBorderWidth,
  CssFramePaddingString,
} from "../MellowFrameHeader/styles.scss";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name SimpleContentTemplateClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const SimpleContentTemplateClassName = "simple-content";

/**
 *
 * @name SimpleContentTemplateStyle
 * @author Peter Laxalt
 *
 */
export const SimpleContentTemplateStyle = styled.nav`
  &.${SimpleContentTemplateClassName} {
    background: honeydew;
    padding: calc(${CssFramePaddingString} + ${CssFrameBorderWidth});

    &.${SimpleContentTemplateClassName}--has-global-sidebar {
      .${SimpleContentTemplateClassName}__inner {
        display: flex;
        flex-wrap: nowrap;
      }

      .${SimpleContentTemplateClassName}__sidebar {
        background: blue;

        &__el {
          position: sticky;
          top: calc(${CssFramePaddingString} + ${CssFrameBorderWidth});
        }

        &__component {
          position: absolute;
          left: 0;
          top: 0;
        }
      }
    }
  }
`;
