// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import {} from "../../../constants/Theme";
import {} from "../../../constants/Root";
import {} from "../../../constants/styles/CssUtils";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name MainCanvasClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const MainCanvasClassName = "interactive-frame-header";

/**
 *
 * @name MainCanvasStyle
 * @author Peter Laxalt
 *
 */
export const MainCanvasStyle = styled.div`
  &.${MainCanvasClassName} {
    &__inner {
      width: 100vw;
      height: 100vh;
      display: grid;
      place-items: center;
      background: goldenrod;
    }
  }
`;
