/**
 *
 * Shader fun
 * @author Peter Laxalt
 *
 */

import p5, { Font } from "p5";
import { LXLT_ColorTheme } from "../../constants/styles/Color";

// Begin Component
// __________________________________________________________________________

export type LXLT_P5Canvas = {
  W: number;
  H: number;
  ID: number;
  CanvasTheme: LXLT_ColorTheme;
};

export type LXLT_P5CanvasDisplay = (
  W: number,
  H: number,
  ID: number,
  CanvasTheme: LXLT_ColorTheme
) => (p: p5) => void;

const P5CanvasDisplay: LXLT_P5CanvasDisplay = (
  W,
  H,
  ID,
  CanvasTheme
) => (p: p5) => {
  // _________________________________________________
  // Config

  // ____________________________
  // Fonts
  let dharmaFont: Font;

  // ____________________________
  // Typography
  let text = "LAXALT";

  let splitCharacterArray = text.split("");
  let characters: LXLT_P5CanvasChar[] = splitCharacterArray.map(
    (character: string, idx: number) => {
      return {
        letter: character,
        count: 1,
        idx: idx,

        copies: 5,
        copiesLastVisible: 0,
        copiesAreVisible: false,

        countIsIncrementing: true,
        countIsDecrementing: false,

        copiesAreIncrementing: true,
        copiesAreDecrementing: false,
      };
    }
  );

  let characterCount: number = splitCharacterArray.length;
  let characterWidth: number = W / characterCount;
  let characterSize: number = H * 1.25;

  console.log(characterSize, "characterSize");

  // ____________________________
  // Counts
  // let renderCount: number = 0;
  let previousCountUpdateTime: number = 0;
  let previousCopyUpdateTime: number = 0;

  // ____________________________
  // Strokes
  let strokeWidth: number = 4;
  let strokeOffset: number = strokeWidth * 1.5;

  // ____________________________
  // Limits
  let maxCount = 3;
  let minCount = 1;

  let maxCopies = 12;
  let minCopies = 1;

  let frameRate = 10;

  // ____________________________
  // Translation
  // let previousVerticalTranslation: number = H / 2.25;

  let verticalTranslationWhitespaceCompensation: number = 2.25;
  const previousHorizontalTranslation = (idx: number) =>
    -60 + (characterWidth * idx + characterWidth / 1.5 + strokeOffset);

  // _________________________________________________
  // Preload
  p.preload = () => {
    dharmaFont = p.loadFont(`/fonts/dharma/dharma_regular.ttf`);
  };

  // _________________________________________________
  // Setup
  p.setup = () => {
    // Our Canvas
    p.createCanvas(W, H);
  };

  // _________________________________________________
  // Draw
  p.draw = () => {
    p.fill("red");
    p.ellipse(p.mouseX, p.mouseY, 100, 100);
  };

  p.windowResized = () => {
    p.resizeCanvas(W, H);
  };
};

export default P5CanvasDisplay;
