// Core
import React, { Component } from "react";

// Utils
import dynamic from "next/dynamic";

// Theme
import { Theme } from "../../constants/Theme";

// Types
import { LXLT_GLSL_Canvas } from "../../sketches/p5/glsl";
import { createGlobalStyle } from "styled-components";
import { InteractiveFrameHeader } from "../../components/_interactive/InteractiveFrameHeader";
import { LXLT_DharmaTheme } from "../../sketches/p5/dharma";

// Begin Component
// __________________________________________________________________________

type LXLT_P5Wrapper = any;

type LXLT_P5Page = {};
type LXLT_P5Page_State = {
  domLoaded: boolean;
  windowWidth: number;
  windowHeight: number;
  dharmaTheme: LXLT_DharmaTheme;
};

const P5Wrapper: LXLT_P5Wrapper = dynamic(import("react-p5-wrapper"), {
  loading: () => <p>Loading...</p>,
  ssr: false,
});

class P5Page extends Component<LXLT_P5Page, LXLT_P5Page_State> {
  constructor(props) {
    super(props);

    this.state = {
      domLoaded: false,
      windowWidth: 0,
      windowHeight: 0,
      dharmaTheme: {
        background: Theme.Color.Deepsea,
        primary: Theme.Color.White,
        secondary: Theme.Color.Clay,
        foreground: Theme.Color.White,
      },
    };
  }

  componentDidMount() {
    if (typeof window) {
      this.setState({
        windowWidth: window.innerWidth - 80,
        windowHeight: window.innerHeight - 80,
        domLoaded: true,
      });
    }
  }

  renderP5 = (filename: string) => {
    const sketch: LXLT_GLSL_Canvas = require(`../../sketches/p5/${filename}`).default(
      this.state.windowWidth,
      this.state.windowHeight,
      1,
      this.state.dharmaTheme
    );

    return <P5Wrapper sketch={sketch} />;
  };

  render() {
    const GlobalScrollLock = createGlobalStyle`
      body, html {
        overflow: hidden;
      }
    `;

    return (
      <>
        <GlobalScrollLock />
        <button
          style={{
            position: "fixed",
            right: 100,
            bottom: 100,
            zIndex: 999,
            background: "red",
            color: "white",
            padding: 15,
          }}
        >
          Change Color
        </button>

        <InteractiveFrameHeader />
        {this.state.domLoaded &&
        this.state.windowHeight > 10 &&
        this.state.windowWidth > 10 ? (
          <div
            className="dharma-container"
            style={{
              width: "100vw",
              height: "100vh",
              overflow: "hidden",
              display: "flex",
              justifyContent: "center",
              alignItems: "center",
            }}
          >
            {this.renderP5("dharma")}
          </div>
        ) : null}
      </>
    );
  }
}

export default P5Page;
