/**
 *
 * /components/25/Counter.tsx
 * @author Peter Laxalt
 * @description Counter styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled from "styled-components";

// Constants
// import { Theme } from "../../constants/Theme";
// import { Root } from "../../constants/Root";

// Begin Styles
//////////////////////////////////////////////////////////////////////

const baseCounterSize = "12vw";

export const CounterStyle = styled.div`
  position: fixed;
  left: 50px;
  bottom: 150px;
  z-index: 300;
  font-family: "Portia";
  pointer-events: none;
  width: ${baseCounterSize};
  height: ${baseCounterSize};
  background: black;

  .number {
    position: relative;

    &:before {
      content: "";
      position: absolute;
      left: 50%;
      top: 50%;
      transform: translate(-50%, -50%) rotate(45deg);
      transform-origin: center center;
      width: 1px;
      height: ${baseCounterSize};
      background: white;
    }
    span {
      &:first-child {
        position: absolute;
        left: 0;

        font-weight: bold;
        font-size: calc(${baseCounterSize} * .7);
      }

      &:last-child {
        font-size: calc(${baseCounterSize} * .25);
      }
    }
  }
`;
