    img {
      max-width: 100%;
      line-height: 0;
    }

    .listing-title {
      line-height: 1.2;
      display: flex;
      width: 100%; 
      text-align: center;
      color: ${Root.Color.Mbm}
      font-size: 16px;
    }
  }
`;
