import React, { Component, createRef } from "react";

import { createGlobalStyle } from "styled-components";
import { FadeIn } from "../../constants/styles/Animation";

import { InteractiveFrameHeader } from "../../components/_interactive/InteractiveFrameHeader";

import { canvas } from "../../sketches/three/t2";

import { NextPage } from "next";
import { ColorContext, LXLT_ColorTheme } from "../../constants/styles/Color";

// ______________________________________________________________

type LXLT_ThreePage = {
  colorThemeContext: LXLT_ColorTheme;
};

type LXLT_ThreePage_State = {
  domLoaded: boolean;
  windowWidth: number;
  windowHeight: number;
};

class ThreePageWithContext extends Component<
  LXLT_ThreePage,
  LXLT_ThreePage_State
> {
  constructor(props) {
    super(props);

    this.state = {
      domLoaded: false,
      windowWidth: 0,
      windowHeight: 0,
    };
  }

  private threeRootElement = createRef<HTMLDivElement>();

  componentDidMount() {
    if (typeof window) {
      canvas(this.threeRootElement);

      this.setState({
        windowWidth: window.innerWidth - 80,
        windowHeight: window.innerHeight - 80,
        domLoaded: true,
      });
    }
  }

  render() {
    const ThreePageGlobalStyles = createGlobalStyle`
      body, html {
        overflow: hidden;
      }

      .dharma-container {
        width: 100vw;
        height: 100vh;
        overflow: hidden;
        display: flex;
        justify-content: center;
        align-items: center;

        canvas {
          animation: ${FadeIn} 1s ease 1;
          animation-fill-mode: forwards;
          animation-iteration-count: 1;
        }

        #p5_loading {
          display: none !important;
        }
      }
    `;

    return (
      <>
        <ThreePageGlobalStyles />
        <InteractiveFrameHeader />
        <div
          ref={this.threeRootElement}
          style={{ width: "100vw", height: "100vh" }}
        />
      </>
    );
  }
}

export const ThreeCanvasPage: NextPage = () => {
  const themeContext = React.useContext(ColorContext);

  return <ThreePageWithContext colorThemeContext={themeContext} />;
};

export default ThreeCanvasPage;
