// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../../constants/Theme";
import {} from "../../../constants/Root";
import { CssFrameSizeWithBorderString } from "../../MellowFrameHeader/styles.scss";
import {
  LayeredSidebarWidth,
  SidebarFullwidthCss,
} from "../../LayeredSidebar/styles.scss";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name ProjectScrollSectionClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const ProjectScrollSectionClassName = "project-scroll";

/**
 *
 * @name ProjectScrollSectionStyle
 * @author Peter Laxalt
 *
 */
export const ProjectScrollSectionStyle = styled.section`
  &.${ProjectScrollSectionClassName} {
    &.${ProjectScrollSectionClassName}--has-sidebar {
      /* ${SidebarFullwidthCss} */
      width: calc(100vw - (${CssFrameSizeWithBorderString} * 2));
      margin-left: calc(${LayeredSidebarWidth} * -1);

      /* padding-left: ${LayeredSidebarWidth}; */

      @media (max-width: ${Theme.Base.Media.Width.Sm}) {
        width: 100%;
        max-width: 100%;
        margin-left: 0;

        .spacer {
          display: none;
        }
      }
    }

    .${ProjectScrollSectionClassName}__sticky-inner {
      position: sticky;
      top: 0;
      height: 100vh;
      width: 100%;
      overflow-x: hidden;
    }
  }
`;

// @ts-ignore
export const ProjectScrollTallOuterContainerStyle = styled.div.attrs(
  ({ dynamicHeight }) => ({
    style: { height: `${dynamicHeight}px` },
  })
)`
  position: relative;
  width: 100%;
`;

export const ProjectScrollStickyInnerContainerStyle = styled.div`
  position: sticky;
  top: 0;
  height: 100vh;
  width: 100%;
  overflow-x: hidden;
`;

// @ts-ignore
export const ProjectScrollHorizontalTranslateContainerStyle = styled.div.attrs(
  ({ translateX }) => ({
    style: { transform: `translateX(${translateX}px)` },
  })
)`
  position: absolute;
  height: 100%;
  transition: transform 0.5s ease-out;
  will-change: transform;
`;

export const ProjectScrollContentWrapperStyle = styled.div`
  position: relative;
  height: 100vh;
  padding: 0;
  display: flex;
  flex-flow: row nowrap;
  justify-content: flex-start;
  align-items: center;
`;

export const ProjectScrollHorizontalSectionStyle = styled.section`
  position: relative;
  width: 100%;
  min-height: 100vh;
`;
