// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../constants/Theme";
import {} from "../../constants/Root";
import {} from "../../constants/styles/CssUtils";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name ThemePickerClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const ThemePickerClassName = "theme-picker";

/**
 *
 * @name ThemePickerStyle
 * @author Peter Laxalt
 *
 */
export const ThemePickerStyle = styled.div`
  &.${ThemePickerClassName} {
    position: fixed;

    left: 0;
    top: 50%;

    transform: translateY(-50%);

    border-right: 1px solid ${Theme.Color.Primary};
    border-top: 1px solid ${Theme.Color.Primary};
    border-bottom: 1px solid ${Theme.Color.Primary};

    background: ${Theme.Color.Background};

    z-index: 999
  }
`;
