/**
 *
 * Layout.js
 * @author Peter Laxalt
 * @description The website layout.
 *
 */

// Constants
import React, { Component } from "react";
import { GlobalStyle } from "../../constants/styles/Global";

// Components
// import { Footer } from "../Footer";
import { Navigation } from "../Navigation";
import { SiteHead } from "../SiteHead";

// Begin Interface
//////////////////////////////////////////////////////////////////////

interface Layout {
  children?: any;
  title?: string;
}

// Begin Component
//////////////////////////////////////////////////////////////////////

// Cursor
class Cursor extends Component<{}, any> {
  constructor(props: any) {
    super(props);

    this.state = {
      mouseX: 0,
      mouseY: 0,
      xPos: 0,

      dx: 0,
      dy: 0,
      mouseSize: 20
    };
  }

  componentDidMount() {
    const setMousePosition = (e: any) => {
      // let dX = this.state.x - xPos;
      // let dY = this.state.Y - xPos;

      // xPos += dX / 10;
      // yPos += dY / 10;

      this.setState({
        x: e.clientX,
        y: e.clientY
      });

      console.log("x: " + this.state.x, "y: " + this.state.y);
    };

    document.addEventListener("mousemove", setMousePosition, false);
  }

  render() {
    let xPos = this.state.mouseX - this.state.mouseSize / 2;
    let yPos = this.state.mouseY - this.state.mouseSize / 2;

    return (
      <div
        className="cursor"
        style={{
          zIndex: 999,
          position: "fixed",
          left: 0,
          top: 0,
          pointerEvents: "none",
          transform: "translate(" + xPos + "px," + yPos + "px)",
          width: this.state.mouseSize,
          height: this.state.mouseSize,
          mixBlendMode: "exclusion",
          borderRadius: "50%",
          backgroundColor: "white"
        }}
      />
    );
  }
}

// Layout

export const Layout = ({ children }: Layout) => (
  <>
    <Cursor />
    <GlobalStyle />
    <SiteHead title="L&M 6" />
    <Navigation />
    <main>{children}</main>
    {/* <Footer /> */}
  </>
);
