/**
 *
 * Navigation.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website Navigation Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../constants/Theme";
import { Root } from "../../constants/Root";

// Begin Styles
//////////////////////////////////////////////////////////////////////

export const NavigationStyle = styled.nav`
  color: ${Root.Color.Mbm};
  mix-blend-mode: exclusion;

  a {
    text-transform: uppercase;
    color: ${Root.Color.Mbm};
  }

  .top {
    display: flex;
    justify-content: space-between;

    .left,
    .right,
    .center {
      height: ${Root.Nav.Size};
      top: 0;
      position: fixed;
    }

    .left {
      display: flex;
      flex-direction: column;
      left: 0;
      padding-left: ${Root.Grid.Gutter.Left};
      text-transform: uppercase;
      justify-content: center;

      span {
        &:first-child {
          font-weight: bold;
        }
        &:last-child {
          font-size: 11px;
        }
      }
    }

    .right {
      display: flex;
      flex-direction: column;
      left: 0;
      padding-left: ${Root.Grid.Gutter.Left};
      text-transform: uppercase;
      justify-content: center;

      span {
        &:first-child {
          font-weight: bold;
        }
        &:last-child {
          font-size: 11px;
        }
      }
    }

    .center {
      display: flex;
      width: 100%;
      justify-content: center;
      align-items: center;
      height: 50px;
      font-size: 40px;
      text-transform: uppercase;
      font-weight: bold;
      left: 50%;
      transform: translateX(-50%);
    }
  }

  .bottom {
    position: fixed;
    top: calc(${Root.Nav.Size} + 20px);
    left: 0;
    right: 0;

    .wrapper {
      max-width: ${Theme.Base.Grid.SiteWidth};
      width: 100%;
      margin: 0 auto;
      padding-left: ${Root.Grid.Gutter.Left};
      padding-right: ${Root.Grid.Gutter.Right};
      display: flex;
      justify-content: space-between;
    }
  }
`;
