/**
 *
 * MarqueeRow.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website MarqueeRow Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled, { keyframes } from "styled-components";

// Constants
import { Root } from "../../../constants/Root";
import {  } from "../../../constants/styles/Animation";
import { Theme } from "../../../constants/Theme";

// Helpers

// Begin Styles
//////////////////////////////////////////////////////////////////////

export const MarqueeRowClassName = `marquee-row`;


export const MarqueeRowAnim = keyframes`
  from {
    transform: translateX(0%);
  }
  to {
    transform: translateX(-100%);
  }
`;


export const MarqueeRowStyle = styled.section`
  --${MarqueeRowClassName}__offset: 20vw;
  --${MarqueeRowClassName}__move-initial: calc(-25% + var(--${MarqueeRowClassName}__offset));
  --${MarqueeRowClassName}__move-final: calc(-50% + var(--${MarqueeRowClassName}__offset));

  position: relative;
  overflow: hidden;

  .${MarqueeRowClassName}__inner {
    width: fit-content;
    display: flex;
    position: relative;
    transform: translate3d(var(--${MarqueeRowClassName}__move-initial), 0, 0);
    animation: ${MarqueeRowAnim} 5s linear infinite;
    animation-play-state: running;
  }

  .${MarqueeRowClassName}__list {
    &__item {
      font-size: 10vw;
      padding: 0 2vw;
    }
  }

  &:hover {
    .${MarqueeRowClassName}__inner {
      animation-play-state: paused;
    }
  }

`;
