// ____________________________________________________________________________________

export type LXLT_WaterTextureOptions = {
  debug?: boolean;
};

/**
 *
 * @name WaterTexture
 * @see https://tympanus.net/codrops/2019/10/08/creating-a-water-like-distortion-effect-with-three-js/
 *
 */

export class WaterTexture {
  // _________________________________
  // Types
  size: number;
  radius: number;
  width: number;
  height: number;
  canvas: HTMLCanvasElement;
  ctx: CanvasRenderingContext2D;
  debug?: boolean;
  points: {
    x: number;
    y: number;
    age: number;
  }[];
  maxAge: number;

  // _________________________________
  // Constructor
  constructor(options: LXLT_WaterTextureOptions) {
    this.size = 64;
    this.radius = this.size * 0.1;
    this.width = this.height = this.size;

    this.size = 64;
    this.radius = this.size * 0.1;

    this.points = [];
    this.maxAge = 64;

    if (options.debug) {
      this.width = window.innerWidth;
      this.height = window.innerHeight;
      this.radius = this.width * 0.05;
    }

    this.initTexture();
    if (options.debug) document.body.append(this.canvas);
  }
  // _________________________________
  // Initialize our canvas
  initTexture() {
    this.canvas = document.createElement("canvas");
    this.canvas.id = "WaterTexture";
    this.canvas.width = this.width;
    this.canvas.height = this.height;
    this.ctx = this.canvas.getContext("2d");
    this.clear();
  }

  addPoint(point) {
    this.points.push({ x: point.x, y: point.y, age: 0 });
  }

  clear() {
    this.ctx.fillStyle = "black";
    this.ctx.fillRect(0, 0, this.canvas.width, this.canvas.height);
  }

  update() {
    this.clear();
    this.points.forEach((point) => {
      point.age += 1;
      if (point.age > this.maxAge) {
        this.points.splice(i, 1);
      }
    });
    this.points.forEach((point) => {
      this.drawPoint(point);
    });
  }
}
