/**
 *
 * Cursor.js
 * @author Peter Laxalt
 * @description The website Cursor.
 *
 */

// Core
import React, { Component } from "react";

// Styles
import { CursorStyle } from "./styles.scss";

// Begin Component
//////////////////////////////////////////////////////////////////////

// Cursor
export class Cursor extends Component<{}, any> {
  constructor(props: any) {
    super(props);

    this.state = {
      mouseX: 0,
      mouseY: 0,
      xPos: 0,
      yPos: 0,
      dx: 0,
      dy: 0,
      mouseSize: 20
    };
  }

  componentDidMount() {
    /**
     *
     * @name Set Cursor Position
     * @param e : Event from "mousemove" event listener.
     * @description This positions the Cursor throughout the page.
     *
     */
    const setCursorPosition = (e: any) => {
      let xPos = this.state.mouseX - this.state.mouseSize / 2;
      let yPos = this.state.mouseY - this.state.mouseSize / 2;

      let dX = this.state.mouseX - this.state.xPos;
      let dY = this.state.mouseY - this.state.yPos;

      this.setState({
        xPos: xPos + dX / 10,
        yPos: yPos + dY / 10,
        mouseX: e.clientX,
        mouseY: e.clientY
      });

      console.log("x: " + this.state.mouseX, "y: " + this.state.mouseY);
    };

    /**
     *
     * @name Scale Cursor
     * @param e : Event from "mousedown" event listener.
     * @description This scales the mouse size.
     *
     */
    const scaleCursor = (scale: number) => {
      this.setState({
        mouseSize: scale
      });
    };

    /**
     *
     * @name Expand Cursor Size
     * @param e : Event from "mousedown" event listener.
     * @description This scales the cursor size up.
     *
     */
    const expandCursorSize = () => {
      scaleCursor(2);
      return;
    };

    /**
     *
     * @name Reduce Cursor Size
     * @param e : Event from "mousedown" event listener.
     * @description This scales the cursor size down.
     *
     */
    const reduceCursorSize = () => {
      scaleCursor(1);
    };

    document.addEventListener("mousemove", setCursorPosition, false);
    document.addEventListener("mousedown", expandCursorSize, false);
    document.addEventListener("mouseup", reduceCursorSize, false);
  }

  render() {
    return (
      <CursorStyle
        className="cursor"
        style={{
          transform:
            "translate(" + this.state.xPos + "px," + this.state.yPos + "px)",
          width: this.state.mouseSize,
          height: this.state.mouseSize
        }}
      >
        <div className="cursor-inner" />
      </CursorStyle>
    );
  }
}
