// Core
import React from "react";
import { CSSProperties } from "styled-components";
import { LXLT_SimpleContentNode } from "../../SimpleContentTemplate";

// Styles
import { StaggeredBlocksSectionClassName, StaggeredBlocksSectionStyle } from "./styles.scss";

// Begin Component
// __________________________________________________________________________________________

export type LMNTS_Section_StaggeredBlocksSection = LXLT_SimpleContentNode & {
  hasSidebar?: boolean;
};

/**
 *
 * @name StaggeredBlocksSection
 * @author Peter Laxalt
 * @requires /studio/sections/StaggeredBlocksSection
 *
 */
export const StaggeredBlocksSection: React.FunctionComponent<LMNTS_Section_StaggeredBlocksSection> = ({
  style,
  hasSidebar,
}) => {
  return (
    <StaggeredBlocksSectionStyle
      style={style}
      className={`${StaggeredBlocksSectionClassName} ${StaggeredBlocksSectionClassName}--${
        hasSidebar ? `has-sidebar` : `sans-sidebar`
      }`}
    >
      <div className={`${StaggeredBlocksSectionClassName}__inner`}>
        STAGGERED BLOCKS SECTION
      </div>
    </StaggeredBlocksSectionStyle>
  );
};

// End Component
// __________________________________________________________________________________________
