/**
 *
 * /components/25/Counter.tsx
 * @author Peter Laxalt
 * @description Counter styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled from "styled-components";

// Constants
// import { Theme } from "../../constants/Theme";
// import { Root } from "../../constants/Root";

// Begin Styles
//////////////////////////////////////////////////////////////////////

export const CounterStyle = styled.div`
  position: fixed;
  left: 50px;
  bottom: 150px;
  z-index: 300;
  font-family: "Portia";

  .number {
    position: relative;

    &:before {
      content: "";
      position: absolute;
      left: 50%;
      top: 50%;
      transform: translate(-50%, -50%);
      width: 1px;
      height: 80px;
      background: white;
    }
    span {
      &:first-child {
        font-weight: bold;
        font-size: 9vw;
      }

      &:last-child {
        font-size: 4vw;
      }
    }
  }
`;
