// Core
import React from "react";
import LazyImage from "../../../utils/lazyImage";
import { DuotoneImage } from "../../DuotoneImage";
import { LXLT_SimpleContentNode } from "../../SimpleContentTemplate";

// Styles
import { StretchIntroSectionClassName, StretchIntroSectionStyle } from "./styles.scss";

// Begin Component
// __________________________________________________________________________________________

export type LMNTS_Section_StretchIntroSection = LXLT_SimpleContentNode & {
  hasSidebar?: boolean;
};

/**
 *
 * @name StretchIntroSection
 * @author Peter Laxalt
 * @requires /studio/sections/StretchIntroSection
 *
 */
export const StretchIntroSection: React.FunctionComponent<LMNTS_Section_StretchIntroSection> = ({
  hasSidebar,
}) => {
  return (
    <StretchIntroSectionStyle
      className={`${StretchIntroSectionClassName} ${StretchIntroSectionClassName}--${
        hasSidebar ? `has-sidebar` : `sans-sidebar`
      }`}
    >
      <div className={`${StretchIntroSectionClassName}__inner`}>
        {/* ____________________________________________________________ */}
        {/* Column */}
        <div
          className={`${StretchIntroSectionClassName}__col ${StretchIntroSectionClassName}__col--focus`}
        >
          <div className={`${StretchIntroSectionClassName}__media`}>
            <DuotoneImage
              src={"/img/headshot.jpg"}
              alt="Peter Laxalt"
              aspectRatio={0.8585131894}
              addClass={`${StretchIntroSectionClassName}__media__image`}
            />
          </div>
        </div>

        {/* ____________________________________________________________ */}
        {/* Column */}
        <div
          className={`${StretchIntroSectionClassName}__col ${StretchIntroSectionClassName}__col--content`}
        >
          <div className={`${StretchIntroSectionClassName}__content`}>
            <p
              className={`${StretchIntroSectionClassName}__content__item ${StretchIntroSectionClassName}__content__item--intro`}
            >
              <strong>Brooklyn, NYC</strong> â€” Peter Laxalt is a
              multidisciplinary director, strategist, designer, educator,
              artist, and creative technologist.
            </p>
            <p className={`${StretchIntroSectionClassName}__content__item`}>
              He works with leading brands, impactful companies, sustainable
              businesses, passionate agencies, driven startups, and loving
              people all over the world. Somehow, he also finds time to pursue
              his various personal ventures, experiments, and lettering. Like
              what you're viewing now.
            </p>
            <p className={`${StretchIntroSectionClassName}__content__item`}>
              Simply put, he believes design isn't one skill or one vision, but
              an understanding of many disciplines & visions.
            </p>
          </div>
        </div>
      </div>
    </StretchIntroSectionStyle>
  );
};

// End Component
// __________________________________________________________________________________________
