// Imports
// _________________________________________________________________________

// Core
import styled from "styled-components";

// Constants
import {} from "../../../constants/Theme";
import {} from "../../../constants/Root";
import { DharmaTypeClassName } from "../../_svg/DharmaType";

// Begin Styles
// _________________________________________________________________________

/**
 *
 * @name InteractiveFrameHeaderClassName
 * @description The root class name for styling. BEM naming conventions.
 *
 */
export const InteractiveFrameHeaderClassName = "interactive-frame-header";

/**
 *
 * @name InteractiveFrameHeaderStyle
 * @author Peter Laxalt
 *
 */
export const InteractiveFrameHeaderStyle = styled.div`
  &.${InteractiveFrameHeaderClassName} {
    --frameEdgeSize: 3rem;

    .${InteractiveFrameHeaderClassName}__inner {
      width: 100vw;
      height: 100vh;

      position: relative;
    }

    .${DharmaTypeClassName}, .${InteractiveFrameHeaderClassName}__frame {
      position: absolute;

      top: 0;
      left: 0;
      right: 0;
      bottom: 0;

      width: 100%;
      height: 100%;
    }

    .${InteractiveFrameHeaderClassName}__type {
      position: absolute;

      top: 50%;
      left: 50%;

      transform: translate(-50%, -50%);

      width: calc(100% - (var(--frameEdgeSize) * 2));
      height: calc(100% - (var(--frameEdgeSize) * 2));


      .${DharmaTypeClassName} {
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;

        width: 100%;
        height: 100%;
      }
    }

    .${InteractiveFrameHeaderClassName}__frame {
      &__y-axis,
      &__x-axis {
        position: absolute;

        overflow: hidden;

        left: 50%;
        top: 50%;
      }

      &__y-axis {
        width: 100vw;
        height: 100vh;

        transform: translate(-50%, -50%);
      }

      &__x-axis {
        width: 100vh;
        height: 100vw;

        transform: translate(-50%, -50%) rotate(90deg);
      }

      &__edge {
        font-size: var(--frameEdgeSize);
        text-transform: uppercase;

        .${InteractiveFrameHeaderClassName}__frame-items {
          list-style-type: none;

          width: 100%;

          display: flex;
          align-items: center;
          justify-content: flex-start;

          li {
            margin-right: calc(var(--frameEdgeSize) / 2);
          }
        }

        &--top,
        &--bottom,
        &--left,
        &--right {
          position: absolute;

          overflow: hidden;
        }

        &--top,
        &--bottom {
          width: calc(100vw - var(--frameEdgeSize));
        }

        &--left,
        &--right {
          width: calc(100vh - var(--frameEdgeSize));
        }

        &--top {
          /* background: seashell; */

          top: 0;
          left: var(--frameEdgeSize);
        }

        &--bottom {
          /* background: magenta; */

          bottom: 0;
          right: var(--frameEdgeSize);
        }

        &--right {
          /* background: orange; */

          top: 0;
          left: var(--frameEdgeSize);

          .${InteractiveFrameHeaderClassName}__frame-items {
            justify-content: flex-end;
          }
        }

        &--left {
          /* background: goldenrod; */

          bottom: 0;
          left: 0;
        }
      }
    }
  }
`;
