/**
 *
 * Footer.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website Footer Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled from "styled-components";
import { Root } from "../../constants/Root";
import { Theme } from "../../constants/Theme";

// Constants
// import { Root } from "../../constants/Theme";

// Begin Styles
//////////////////////////////////////////////////////////////////////

export const FooterClassName = "footer";

export const FooterStyle = styled.footer`
  &.${FooterClassName} {
    position: relative;

    z-index: 10;

    background: ${Theme.Color.varBackground};
    color: ${Theme.Color.varForeground};

    border-top: 2px solid ${Theme.Color.varForeground};

    font-size: 1.5rem;
    font-weight: 200;

    .${FooterClassName}__bottom {
      border-top: 2px solid ${Theme.Color.varForeground};
      padding: ${Root.Size};
    }

    .${FooterClassName}__bottom {
      display: flex;
      justify-content: space-between;

      &__col {
        width: calc(100% / 3);

        &__label {
          display: block;
        }

        strong {
          font-weight: 600;
        }

        &--three {
          text-align: right;
        }
      }
    }
  }
`;
