// Core
import Link from "next/link";
import React, { Component } from "react";
import { __DEBUG__ } from "../../../constants/site/Settings";
import { ThemePicker } from "../../ThemePicker";

// Styles
import {
  InteractiveFrameHeaderClassName,
  InteractiveFrameHeaderStyle,
  LayeredSidebarClassName,
  LayeredSidebarStyle,
} from "./styles.scss";

// Begin Types
// __________________________________________________________________________________________

type LXLT_InteractiveFrameHeaderDisplay = {
  isInactive?: boolean;
  isExpanded?: boolean;
  isOverlayVisible?: boolean;
  isSidebarVisible?: boolean;

  eyeball?: {
    translateX: number;
    translateY: number;
  };
};

type LXLT_InteractiveFrameHeader = {};
type LXLT_InteractiveFrameHeader_State = LXLT_InteractiveFrameHeaderDisplay & {};

type LXLT_InteractiveFrameItem = {
  label: string;
  href?: string;
};

type LXLT_SidebarPaneItem = {
  label: string;
  href: string;
  isMajor: boolean;
};

type LXLT_SidebarPane = {
  header?: string;
  items?: LXLT_SidebarPaneItem[];
};

// Begin Data
// __________________________________________________________________________________________

const FrameItems = () => {
  let items: LXLT_InteractiveFrameItem[] = [
    {
      label: "Studio",
      href: "/",
    },
    {
      label: "Art",
      href: "/",
    },
    {
      label: "Design",
      href: "/",
    },
    {
      label: "Strategy",
      href: "/",
    },
    {
      label: "Interactive",
      href: "/",
    },
    {
      label: "Murals",
      href: "/",
    },
    {
      label: "Code",
      href: "/",
    },
    {
      label: "Lab",
      href: "/",
    },
    {
      label: "Products",
      href: "/",
    },
    {
      label: "Music",
      href: "/",
    },
    {
      label: "Tattoo",
      href: "/",
    },
    {
      label: "Shop",
      href: "/",
    },
    {
      label: "Resources",
      href: "/",
    },
    {
      label: "Advice",
      href: "/",
    },
    {
      label: "Fonts",
      href: "/",
    },
    {
      label: "Contact",
      href: "/",
    },
  ];

  return (
    <div className={`${InteractiveFrameHeaderClassName}__marquee-container`}>
      <ul className={`${InteractiveFrameHeaderClassName}__frame-items`}>
        {items.map((item: LXLT_InteractiveFrameItem, idx: number) => {
          return <li key={idx}>{item.label}</li>;
        })}
      </ul>

      <ul className={`${InteractiveFrameHeaderClassName}__frame-items`}>
        {items.map((item: LXLT_InteractiveFrameItem, idx: number) => {
          return <li key={idx}>{item.label}</li>;
        })}
      </ul>
    </div>
  );
};

const LayeredSideBar = () => {
  let panes: LXLT_SidebarPane[] = [
    {
      header: "Pane 01",
      items: [
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
      ],
    },

    {
      header: "Pane 02",
      items: [
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
      ],
    },

    {
      header: "Pane 03",
      items: [
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: true,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
        {
          label: "Item",
          href: "/",
          isMajor: false,
        },
      ],
    },
  ];

  if (panes) {
    return (
      <LayeredSidebarStyle className={`${LayeredSidebarClassName}`}>
        <div className={`${LayeredSidebarClassName}__inner`}>
          {panes.map((pane: LXLT_SidebarPane, idx: number) => {
            return (
              <>
                {/* ____________________________________________________________ */}
                {/* Navigaton Pane */}
                <div className={`${LayeredSidebarClassName}__pane`} key={idx}>
                  {/* ____________________________________________________________ */}
                  {/* List header */}
                  {pane.header && (
                    <div className={`${LayeredSidebarClassName}__pane__header`}>
                      Header
                    </div>
                  )}

                  {/* ____________________________________________________________ */}
                  {/* List items */}
                  {pane.items && (
                    <>
                      <ul className={`${LayeredSidebarClassName}__pane__list`}>
                        {pane.items.map(
                          (paneItem: LXLT_SidebarPaneItem, idxx: number) => {
                            return (
                              <li
                                className={`${LayeredSidebarClassName}__pane__list__item ${LayeredSidebarClassName}__pane__list__item--${
                                  paneItem.isMajor ? "major" : "minor"
                                }`}
                                key={idxx}
                              >
                                <Link href={paneItem.href}>
                                  <a>{paneItem.label}</a>
                                </Link>
                              </li>
                            );
                          }
                        )}
                      </ul>
                    </>
                  )}
                </div>
              </>
            );
          })}
        </div>
      </LayeredSidebarStyle>
    );
  } else {
    return null;
  }
};

// Begin Component
// _________________________________________________________________________________________

/**
 *
 * @name InteractiveFrameHeader
 *
 */
export class InteractiveFrameHeader extends Component<
  LXLT_InteractiveFrameHeader,
  LXLT_InteractiveFrameHeader_State
> {
  constructor(props: LXLT_InteractiveFrameHeader) {
    super(props);

    this.state = {
      isInactive: false,
    };

    this.listenToScroll = this.listenToScroll.bind(this);
  }

  /**
   *
   * @name componentDidMount()
   *
   */
  componentDidMount() {
    if (typeof window) {
      window.addEventListener("scroll", this.listenToScroll);
    }
  }

  componentWillUnmount() {
    if (typeof window) {
      window.removeEventListener("scroll", this.listenToScroll);
    }
  }

  listenToScroll() {
    if (typeof window && document) {
      const scrollPosition =
        document.body.scrollTop || document.documentElement.scrollTop;

      if (scrollPosition > 10) {
        this.setState({
          isInactive: true,
        });
      } else {
        this.setState({
          isInactive: false,
        });
      }
    }
  }

  render() {
    return <InteractiveFrameHeaderDisplay isInactive={this.state.isInactive} />;
  }
}

/**
 *
 * @name InteractiveFrameHeaderDisplay
 * @author Peter Laxalt
 *
 */
const InteractiveFrameHeaderDisplay: React.FunctionComponent<LXLT_InteractiveFrameHeaderDisplay> = ({
  isInactive,
}) => (
  <>
    {/* ______________________________________________ */}
    {/* Frame itself */}
    <InteractiveFrameHeaderStyle
      className={`${InteractiveFrameHeaderClassName} ${InteractiveFrameHeaderClassName}--${
        isInactive ? `inactive` : `active`
      }`}
    >
      {/* ______________________________________________ */}
      {/* Inner Grid */}
      <div className={`${InteractiveFrameHeaderClassName}__inner`}>
        {/* ______________________________________________ */}
        {/* Frame */}
        <div className={`${InteractiveFrameHeaderClassName}__frame`}>
          {/* ______________________________________________ */}
          {/* Corner Actions */}
          <div
            className={`${InteractiveFrameHeaderClassName}__frame__corner-action ${InteractiveFrameHeaderClassName}__frame__corner-action--top-left`}
          />
          <div
            className={`${InteractiveFrameHeaderClassName}__frame__corner-action ${InteractiveFrameHeaderClassName}__frame__corner-action--top-right`}
          />
          <div
            className={`${InteractiveFrameHeaderClassName}__frame__corner-action ${InteractiveFrameHeaderClassName}__frame__corner-action--bottom-left`}
          />
          <div
            className={`${InteractiveFrameHeaderClassName}__frame__corner-action ${InteractiveFrameHeaderClassName}__frame__corner-action--bottom-right`}
          />

          {/* ______________________________________________ */}
          {/* Top and bottom (y-axis) items */}
          <div className={`${InteractiveFrameHeaderClassName}__frame__y-axis`}>
            <div
              className={`${InteractiveFrameHeaderClassName}__frame__edge ${InteractiveFrameHeaderClassName}__frame__edge--top`}
            >
              <ThemePicker />
              <FrameItems />
            </div>
            <div
              className={`${InteractiveFrameHeaderClassName}__frame__edge ${InteractiveFrameHeaderClassName}__frame__edge--bottom`}
            >
              <FrameItems />
            </div>
          </div>

          {/* ______________________________________________ */}
          {/* Left and right (x-axis) items */}
          <div className={`${InteractiveFrameHeaderClassName}__frame__x-axis`}>
            <div
              className={`${InteractiveFrameHeaderClassName}__frame__edge ${InteractiveFrameHeaderClassName}__frame__edge--left`}
            >
              <FrameItems />
            </div>

            <div
              className={`${InteractiveFrameHeaderClassName}__frame__edge ${InteractiveFrameHeaderClassName}__frame__edge--right`}
            >
              <FrameItems />
            </div>
          </div>
        </div>
      </div>
    </InteractiveFrameHeaderStyle>

    {/* ______________________________________________ */}
    {/* Sidebar */}
    <LayeredSideBar addClass={`${LayeredSidebarClassName}--${isInactive ? `frame-inactive` : `frame-active`}`} />
  </>
);

// End Component
// ______________________________________________________________________________
