/**
 *
 * Layout.js
 * @author Peter Laxalt
 * @description The website layout.
 *
 */

// Constants
import React, { Component } from "react";
import { GlobalStyle } from "../../constants/styles/Global";

// Components
// import { Footer } from "../Footer";
import { Navigation } from "../Navigation";
import { SiteHead } from "../SiteHead";

// Begin Interface
//////////////////////////////////////////////////////////////////////

interface Layout {
  children?: any;
  title?: string;
}

// Begin Component
//////////////////////////////////////////////////////////////////////

// Cursor
class Cursor extends Component<{}, any> {
  constructor(props: any) {
    super(props);

    this.state = {
      x: 0,
      y: 0,
      dx: 0,
      dy: 0,
      mouseSize: 20
    };
  }

  componentDidMount() {
    const setMousePosition = (e: any) => {
      this.setState({
        x: e.clientX,
        y: e.clientY
      });

      console.log("x: " + this.state.x, "y: " + this.state.y);
    };

    document.addEventListener("mousemove", setMousePosition, false);
  }

  render() {
    let xPos = this.state.x - this.state.mouseSize / 2;
    let yPos = this.state.y - this.state.mouseSize / 2;

    return (
      <div
        className="cursor"
        style={{
          zIndex: 999,
          position: "fixed",
          left: 0,
          top: 0,
          transform: "translate(" + xPos + "px," + yPos + "px)",
          width: 20,
          height: 20,
          borderRadius: "50%",
          backgroundColor: "black"
        }}
      />
    );
  }
}

// Layout

export const Layout = ({ children }: Layout) => (
  <>
    <Cursor />
    <GlobalStyle />
    <SiteHead title="L&M 6" />
    <Navigation />
    <main>{children}</main>
    {/* <Footer /> */}
  </>
);
