/**
 *
 * Navigation.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website Navigation Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled from "styled-components";

// Constants
import { Theme } from "../../constants/Theme";
import { Root } from "../../constants/Root";

// Begin Styles
//////////////////////////////////////////////////////////////////////

let navFontSize = "60px";
let navGutter = "20px";

export const NavigationStyle = styled.nav`
  color: ${Root.Color.Mbm};
  mix-blend-mode: exclusion;

  a {
    text-transform: uppercase;
    color: ${Root.Color.Mbm};
  }

  .top {
    display: flex;
    justify-content: space-between;

    .left,
    .right,
    .center {
      height: calc(${navFontSize} + 10px);
      top: 0;
      position: fixed;
    }

    .left {
      display: flex;
      flex-direction: column;
      left: 0;
      padding-left: ${navGutter};
      text-transform: uppercase;
      justify-content: center;

      figure {
        display: block;
        width: 30px;
        height: 30px;
        position: absolute;
        left: ${navGutter};
        margin: 0;
        padding: 0;
        top: 50%;
        transform: translateY(-50%);
        border-radius: 50%;
        border: 1px solid white;
      }

      span {
        &:first-child {
          font-weight: bold;
        }
        &:last-child {
          margin-top: 5px;
          font-size: 11px;
        }
      }
    }

    .right {
      display: flex;
      flex-direction: column;
      left: 0;
      padding-left: ${Root.Grid.Gutter.Left};
      text-transform: uppercase;
      justify-content: center;

      span {
        &:first-child {
          font-weight: bold;
        }
        &:last-child {
          font-size: 11px;
        }
      }
    }

    .center {
      display: flex;
      width: 100%;
      justify-content: center;
      align-items: center;
      font-size: ${navFontSize};
      text-transform: uppercase;
      font-weight: bold;
      left: 50%;
      transform: translateX(-50%);
    }
  }

  .sub {
    position: fixed;
    top: calc(${navFontSize} + 30px);
    left: 0;
    right: 0;

    .wrapper {
      max-width: ${Theme.Base.Grid.SiteWidth};
      width: 100%;
      margin: 0 auto;
      padding-left: ${Root.Grid.Gutter.Left};
      padding-right: ${Root.Grid.Gutter.Right};
      display: flex;
      justify-content: space-between;
    }
  }

  .left-side,
  .right-side {
    position: fixed;
    top: 0;
    bottom: 0;
    width: ${navFontSize};
    display: flex;
    align-items: center;
    font-size: ${navFontSize};

    a {
      transform-origin: center center;
      line-height: ${navFontSize};
    }
  }

  .left-side {
    left: 0;

    a {
      transform: rotate(-90deg) translateY(-80%);
    }
  }

  .right-side {
    right: 0;
    a {
      transform: rotate(90deg) translateY(170%);
    }
  }

  .bottom {
    .left,
    .right,
    .center {
      display: flex;
      height: calc(${navFontSize} + 14px);
      bottom: 0;
      position: fixed;
      font-size: ${navFontSize};
      text-transform: uppercase;
      align-items: center;
      text-transform: uppercase;
    }

    .left {
      left: 0;
      padding-left: ${navGutter};
      justify-content: flex-start;
    }

    .right {
      right: 0;
      padding-right: ${navGutter};
      justify-content: flex-end;
    }

    .center {
      justify-content: center;
      left: 50%;
      transform: translateX(-50%);
    }
  }
`;
