/**
 *
 * IntroSection.js/styles.scss.tsx
 * @author Peter Laxalt
 * @description The website IntroSection Styles.
 *
 */

// Imports
//////////////////////////////////////////////////////////////////////

// Core
import styled, { keyframes } from "styled-components";

// Constants
import { Root } from "../../../constants/Root";
import { ScaleXIn } from "../../../constants/styles/Animation";
import { CssUtils } from "../../../constants/styles/CssUtils";
import { Theme } from "../../../constants/Theme";
import {} from "../../MellowFrameHeader/styles.scss";

// Helpers

// Begin Styles
//////////////////////////////////////////////////////////////////////

export const IntroSectionClassName = `intro-section`;

export const IntroSectionStyle = styled.section`
  &.${IntroSectionClassName} {
    margin: calc(${Root.Size} * 2) 0;

    .${IntroSectionClassName}__inner {
      display: flex;
      flex-wrap: nowrap;
      justify-content: space-between;

      .${IntroSectionClassName}__col {
        &--focus {
          width: calc(100% * 0.66666666);
        }

        &--content {
          flex: 1;
        }
      }
    }

    .${IntroSectionClassName}__media {
      position: relative;

      width: 100%;
      padding-bottom: calc(100% * 0.8585131894);

      background: ${Theme.Color.varForeground};

      line-height: 0;

      position: relative;

      z-index: 2;

      box-shadow: 0px 0px 0px 2px ${Theme.Color.varForeground};
      transition: box-shadow 1s ease;

      overflow: hidden;

      &:after {
        content: "";

        position: absolute;

        left: 0;
        top: 0;

        width: 100%;
        height: 100%;

        background: ${Theme.Color.varForeground};

        transform: scaleX(1);
        transform-origin: left center;

        transition: transform 1s ease;
      }

      &:before {
        content: "";

        position: absolute;

        left: 0;
        top: 0;

        width: 100%;
        height: 100%;

        background: ${Theme.Color.varBackground};
        mix-blend-mode: multiply;

        z-index: 2;

        opacity: 1;

        transition: opacity 1s ease;
        transition-delay: transform 2.75s ease;
      }

      .${IntroSectionClassName}__media__image {
        &--filtered {
          mix-blend-mode: screen;
          filter: grayscale() blur(0px);

          transition: filter 1s ease;
        }

        &--normal {
          position: absolute;
          left: 0;
          top: 0;

          opacity: 0;

          transition: opacity 1s ease;
        }
      }

      &:hover {
        /* box-shadow: 0px 0px 0px 8px ${Theme.Color.varForeground}; */

        &:before {
          opacity: 0;
        }

        .${IntroSectionClassName}__media__image--filtered {
          mix-blend-mode: screen;
          filter: grayscale() blur(20px);
        }

        .${IntroSectionClassName}__media__image--normal {
          opacity: 1;

          transition: opacity 0.5s ease;
        }
      }

      img {
        ${CssUtils.ObjectFit()};
      }
    }

    .${IntroSectionClassName}__content {
      padding-left: calc(${Root.Size} * 2);

      &__item {
        margin-bottom: 1.5em;

        &--intro {
          font-family: ${Theme.Font.Code};

          font-size: 1.75rem;

          letter-spacing: -1.25px;

          strong {
            text-transform: uppercase;
          }
        }
      }
    }
  }
`;
